expressions.CONTEXT:
{
  currentStep: 1,
  /* Number (Integer) */
  evaluation: rule!AS_GSS_QE_getEvaluation(
    evaluationId: rv!record[recordType!AS_GSS_Evaluation_RECORD.evaluationId],
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT
  ),
  /* AS_GSS_Evaluation */
  evaluationCriterias: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: rv!record[recordType!AS_GSS_Evaluation_RECORD.evaluationId]
  ),
  /* List of AS_GSS_Criteria */
  evaluationVendors: rule!AS_GSS_QE_getEvaluationVendor(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: rv!record[recordType!AS_GSS_Evaluation_RECORD.evaluationId]
  ),
  /* List of AS_GSS_EvaluationVendor */
  isUploadDoc: true,
  /* Boolean */
  includeAllDocTypesForUpload: rule!AS_GSS_BL_getAllDocTypeVisibilityForUpload(
    evaluationStatusId: rv!record[recordType!AS_GSS_Evaluation_RECORD.evaluationStatus.refDataId]
  )
}

data.sail_code:
a!localVariables(
  local!includeAllDocTypesForUpload: a!defaultValue(ri!includeAllDocTypesForUpload, false()),
  local!gssPropertyLabels: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!docTypes: rule!AS_GSS_QE_getRefDataByTypes(
    refTypes: cons!AS_GSS_REF_TYPE_DOCUMENT_TYPE,
    excludeRefDataIds: if(
      local!includeAllDocTypesForUpload,
      null,
      rule!AS_GSS_BL_documentTypesToExcludeFromSelectionOnUpload()
    )
  ),
  local!vendorDocuments,
  local!factorDocuments,
  local!consensusDocuments,
  local!otherDocuments,
  local!defaultFactorId: a!refreshVariable(
    value:
    rule!AS_GSS_BL_defaultFactorDocumentMappingCdt(
    evaluation: ri!evaluation,
    evaluationCriterias: ri!evaluationCriterias
  ),
  refreshOnVarChange:{
    ri!evaluation,ri!evaluationCriterias
  }
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: local!gssPropertyLabels,
      bundleKey: "lbl_UploadDocuments",
      arguments: if(
        rule!AS_CO_UT_isBlank(ri!evaluation.evaluationNumber),
        "",
        " | " & ri!evaluation.evaluationNumber
      )
    ),
    instructions: "Required fields are marked with an asterisk (*)",
    contents: choose(
      ri!currentStep,
      /*Upload doc*/
      rule!AS_GSS_CPS_uploadDocuments(
        evaluationDocuments: ri!evaluationDocuments,
        evaluation: ri!evaluation,
        i18nData: local!gssPropertyLabels,
        vendorId: ri!vendorId,
        refData: local!docTypes
      ),
      /*Select doc type*/
      rule!AS_GSS_GRD_selectDocTypeForUploadedDocs(
        evaluationDocuments: ri!evaluationDocuments,
        docTypes: local!docTypes,
        i18nData: local!gssPropertyLabels,
        vendorId: ri!vendorId,
        isUploadDoc: ri!isUploadDoc
      ),
      /*Associate docs with vendor / criteria*/
      rule!AS_GSS_GRD_associateDocuments(
        evaluationCriterias: ri!evaluationCriterias,
        evaluationVendors: ri!evaluationVendors,
        i18nData: local!gssPropertyLabels,
        vendorDocuments: local!vendorDocuments,
        factorDocuments: local!factorDocuments,
        consensusDocuments: local!consensusDocuments,
        vendorId: ri!vendorId,
        factorDocumentMapping: ri!factorDocumentMapping
      )
    ),
    buttons: rule!AS_CO_CPS_cancelSaveButtons(
      confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: local!gssPropertyLabels,
        bundleKey: if(
          ri!currentStep = 1,
          "btn_Upload",
          if(
            ri!currentStep = 2,
            if(
              ri!isUploadDoc,
              /*As the automation script not able to find the Select button, using different
              bundle key when Automation toggle is ON*/
              if(
                cons!AS_GSS_TOGGLE_AUTOMATION_TESTING_ENABLED,
                "plc_DocumentType",
                "btn_Select"
              ),
              "btn_Confirm"
            ),
            "btn_Submit"
          )
        )
      ),
      cancelLabel: "Cancel",
      confirmSaveInto: {
        a!save(
          ri!userAction,
          if(
            ri!currentStep = 1,
            cons!AS_CO_ENUM_USER_ACTION_UPLOAD,
            cons!AS_CO_ENUM_USER_ACTION_SUBMIT
          )
        ),
        if(
          and(
            ri!currentStep = 2,
            or(
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
              ),
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
              ),
              contains(
                ri!evaluationDocuments.docType.refDataId,
                cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
              )
            )
          ),
          /*Grouping the docs based on doc type*/
          {
            a!save(
              local!vendorDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
              )
            ),
            if(
              rule!AS_CO_UT_isBlank(local!defaultFactorId),
              {},
              a!save(
                ri!factorDocumentMapping,
                append(
                  ri!factorDocumentMapping,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!vendorDocuments,
                    expression: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_FactorDocumentMapping'(
                      document: fv!item,
                      factor: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria'(criteriaId: local!defaultFactorId),
                      createdBy: loggedInUser(),
                      createdDatetime: now(),
                      isActive: true
                    )
                  )
                )
              )
            ),
            a!save(
              local!factorDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
              )
            ),
            a!save(
              local!consensusDocuments,
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!evaluationDocuments,
                field: "docType.refDataId",
                value: cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
              )
            ),
            /*Saving Evaluator and Recommendation docs as other docs*/
            a!save(
              local!otherDocuments,
              index(
                ri!evaluationDocuments,
                wherecontains(
                  {
                    cons!AS_GSS_REF_ID_DOC_TYPE_EVALUATOR,
                    cons!AS_GSS_REF_ID_DOC_TYPE_RECOMMENDATION,
                    cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
                  },
                  ri!evaluationDocuments.docType.refDataId
                ),
                null
              )
            )
          },
          if(
            ri!currentStep >= 3,
            a!save(
              ri!evaluationDocuments,
              {
                local!vendorDocuments,
                local!factorDocuments,
                local!consensusDocuments,
                local!otherDocuments
              }
            ),
            null
          )
        ),
        a!save(ri!currentStep, ri!currentStep + 1)
      },
      cancelSaveInto: a!save(
        ri!userAction,
        cons!AS_CO_ENUM_USER_ACTION_CANCEL
      ),
      confirmSubmit: if(
        /*when no vendor / factor / consensus documents are uploaded, the form will move to the third step instead of submitting*/
        and(
          ri!currentStep = 2,
          or(
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
            ),
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
            ),
            contains(
              ri!evaluationDocuments.docType.refDataId,
              cons!AS_GSS_REF_ID_DOC_TYPE_CONSENSUS
            )
          )
        ),
        false,
        true
      ),
      cancelSubmit: true,
      confirmAccessibilityText: if(
        ri!currentStep = 1,
        "Press Enter to upload file",
        if(
          ri!currentStep = 2,
          "Press Enter to select document type",
          "Press Enter to submit"
        )
      ),
      cancelAccessibilityText: "Press Enter to cancel the upload",
      cancelShowWhen: ri!currentStep = 1
    )
  )
)

a!localVariables(
  /*The button layout, which can dynamically be inside a section or not*/
  /*Note that when using this pattern of storing an interface as a local variable, it must be set to refreshAlways = true*/
  local!buttonLayout: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!confirmAccessibilityText,
          label: ri!confirmLabel,
          size: ri!confirmSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!confirmSaveInto
            ),
            {},
            ri!confirmSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!confirmStyle,
            replacementValue: "PRIMARY"
          ),
          validate: true,
          validationGroup: ri!confirmValidationGroup,
          submit: ri!confirmSubmit,
          disabled: ri!confirmDisabled,
          confirmHeader: ri!confirmHeader,
          confirmMessage: ri!confirmMessage
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          accessibilityText: ri!cancelAccessibilityText,
          label: ri!cancelLabel,
          size: ri!cancelSize,
          saveInto: if(
            rule!AS_CO_UT_isBlank(
              ri!cancelSaveInto
            ),
            {},
            ri!cancelSaveInto
          ),
          style: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!cancelStyle,
            replacementValue: "NORMAL"
          ),
          validate: false,
          submit: ri!cancelSubmit,
          disabled: ri!cancelDisabled,
          confirmHeader: ri!cancelHeader,
          confirmMessage: ri!cancelMessage,
          showWhen: ri!cancelShowWhen
        )
      },
      showWhen: ri!showWhen
    ),
    refreshAlways: true
  ),
  /*Components to show*/
  if(
    ri!includeDivider,
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      divider: "ABOVE",
      contents: {
        local!buttonLayout
      }
    ),
    local!buttonLayout
  )
)

if(
  ri!readOnly,
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    label: ri!label,
    labelPosition: rule!AS_CO_UT_getLabelPosition(
      label: ri!label,
      labelPosition: ri!labelPosition,
      readOnly: ri!readOnly
    ),
    instructions: ri!instructions,
    value: if(
      rule!AS_CO_UT_isBlank(ri!value),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: "AS.CO.CommonObjects.lbl_NoDocuments"
        ),
        style: "EMPHASIS"
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!value,
        expression: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_createDocumentDownloadLinkLabel(document(fv!item, "name")),
            link: #"SYSTEM_SYSRULES_documentDownloadLink"(document: fv!item)
          ),
          if(fv!isLast, null, char(10))
        }
      )
    ),
    helpTooltip: ri!helpTooltip,
    showWhen: ri!showWhen,
    accessibilityText: ri!accessibility
  ),
  #"SYSTEM_SYSRULES_fileUploadField_v1"(
    label: ri!label,
    labelPosition: rule!AS_CO_UT_getLabelPosition(
      label: ri!label,
      labelPosition: ri!labelPosition,
      readOnly: ri!readOnly
    ),
    instructions: ri!instructions,
    helpTooltip: ri!helpTooltip,
    target: ri!target,
    fileNames: ri!fileNames,
    fileDescriptions: ri!fileDescriptions,
    maxSelections: ri!maxSelections,
    value: ri!value,
    saveInto: ri!saveInto,
    required: ri!required,
    /* Updated requiredMessage parameter to show default validation text*/
    requiredMessage: rule!AS_CO_UT_displayRequiredMessage(
      label: ri!label,
      requiredMessage: ri!requiredMessage
    ),
    disabled: ri!disabled,
    validations: rule!AS_CO_UT_rejectNullValuesFromArray(
      array: {
        ri!validations,
        if(
          rule!AS_CO_UT_isBlank(ri!allowedFileSize),
          null,
          rule!AS_CO_UT_fileSizeValidation(
            maxAllowedFileSizeInBytes: ri!allowedFileSize,
            fileSizesToCheck: fv!files.size
          )
        ),
        if(
          rule!AS_CO_UT_isBlank(ri!allowedFileExtensions),
          null,
          if(
            contains(
              ri!allowedFileExtensions,
              fv!files.extension
            ),
            null,
            concat(
              rule!AS_CO_UT_displayCommonObjectBundleLabel(
                bundleKey: "AS.CO.CommonObjects.vld_InvalidFileType"
              ),
              char(32),
              rule!AS_CO_UT_separateListItemsInSentence(
                list: ri!allowedFileExtensions,
                finalSeparator: rule!AS_CO_UT_displayCommonObjectBundleLabel(bundleKey: "AS.CO.CommonObjects.txt_Or")
              )
            )
          )
        )
      }
    ),
    validationGroup: ri!validationGroup,
    buttonStyle: ri!buttonStyle,
    buttonSize: ri!buttonSize,
    showWhen: ri!showWhen,
    accessibilityText: ri!accessibility,
    buttonDisplay: ri!buttonDisplay
  )
)

a!localVariables(
  local!uploadedDocuments,
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              rule!AS_CO_CP_fileUploadField(
                label: "Documents",
                target: if(
                  rule!AS_CO_UT_isBlank(ri!evaluation.folderId),
                  cons!AS_GSS_FLD_TEMP_DOC,
                  tofolder(ri!evaluation.folderId)
                ),
                maxSelections: cons!AS_GSS_MAX_SELECTION_FOR_UPLOAD_DOC_RA,
                value: local!uploadedDocuments,
                saveInto: {
                  local!uploadedDocuments,
                  a!save(
                    ri!evaluationDocuments,
                    #"SYSTEM_SYSRULES_forEach"(
                      items: local!uploadedDocuments,
                      expression: rule!AS_CO_UT_cast(
                        type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_EvaluationDocument',
                        value: {
                          appianDocId: fv!item,
                          vendorId: if(
                            or(
                              rule!AS_CO_UT_isBlank(ri!vendorId),
                              ri!vendorId = 0
                            ),
                            null,
                            ri!vendorId
                          ),
                          docType: if(
                            or(
                              rule!AS_CO_UT_isBlank(ri!vendorId),
                              ri!vendorId = 0
                            ),
                            null,
                            rule!AS_CO_UT_filterCdtByFieldIncludes(
                              cdt: ri!refData,
                              field: "refDataId",
                              value: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
                            )
                          ),
                          sourceApplication: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_R_Data'(
                            refDataId: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GSS
                          )
                        }
                      )
                    )
                  )
                },
                required: true,
                accessibility: "Press enter to open file browser",
                allowedFileExtensions: cons!AS_GSS_TXT_VALID_FILE_TYPES_FOR_EVALUATION_DOCUMENT,
                instructions: rule!AS_GSS_TXT_documentUploadInstructions(
                  bundle: ri!i18nData,
                  showAllowedFileTypes: true(),
                  showMaxDocumentLimit: true(),
                  maxDocumentsAllowed: cons!AS_GSS_MAX_SELECTION_FOR_UPLOAD_DOC_RA
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"()
        }
      )
    }
  )
)

a!localVariables(
  local!evalFactors: ri!evaluationCriterias[wherecontains(true, ri!evaluationCriterias.isActive)],
  {
    /*Vendor documents*/
    #"SYSTEM_SYSRULES_gridLayout"(
      label: "Vendor Documents",
      labelPosition: "ABOVE",
      headerCells: {
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Document Name"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "lbl_Associatewith",
            arguments: "Vendor"
          ) & " *"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "lbl_Associatewith",
            arguments: "Factor/Subfactor(s)"
          ) & " *",
          showWhen: rule!AS_CO_UT_isNotBlank(ri!vendorDocuments)
        )
      },
      columnConfigs: {
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: ""),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: ""),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
          width: "",
          showWhen: rule!AS_CO_UT_isNotBlank(ri!vendorDocuments)
        )
      },
      rows: #"SYSTEM_SYSRULES_forEach"(
        items: ri!vendorDocuments,
        expression: a!localVariables(
          local!currentDocument: fv!item,
          #"SYSTEM_SYSRULES_gridRowLayout"(
            contents: {
              rule!AS_CO_DSP_standardReadOnlyField(value: fv!item.documentName),
              rule!AS_CO_CP_pickerFieldCustom(
                selectedLabels: index(
                  ri!evaluationVendors,
                  wherecontains(
                    fv!item.vendorId,
                    ri!evaluationVendors.vendorId
                  ),
                  "legalName",
                  null
                ),
                suggestFunction: rule!AS_GSS_UT_suggestFunctionForEvalVendorPicker(
                  value: _,
                  evaluationVendors: ri!evaluationVendors
                ),
                value: fv!item.vendorId,
                saveInto: fv!item.vendorId,
                required: true,
                maxSelections: 1,
                readOnly: if(
                  or(
                    rule!AS_CO_UT_isBlank(ri!vendorId),
                    ri!vendorId = 0
                  ),
                  false,
                  true
                )
              ),
              rule!AS_CO_CP_multipleDropdownField(
                choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                  items: ri!evaluationCriterias,
                  expression: {
                    rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
                      criteriaName: fv!item.criteriaName,
                      factorNumber: fv!item.factorNumber
                    ),

                  }
                ),
                choiceValues: local!evalFactors.criteriaId,
                value: rule!AS_GSS_BL_valueForVendorDocFactorAssociationDropdown(
                  factorDocumentMapping: ri!factorDocumentMapping,
                  vendorDocument: local!currentDocument
                ),
                saveInto: rule!AS_GSS_BL_saveIntoForVendorDocFactorAssociationDropdown(
                  factorDocumentMapping: ri!factorDocumentMapping,
                  vendorDocument: local!currentDocument
                ),
                showWhen: rule!AS_CO_UT_isNotBlank(ri!vendorDocuments),
                required: true
              )
            }
          )
        )
      ),
      showWhen: rule!AS_CO_UT_isNotBlank(ri!vendorDocuments),
      validations: {},
      shadeAlternateRows: true,
      borderStyle: ""
    ),
    /*Factor documents*/
    #"SYSTEM_SYSRULES_gridLayout"(
      label: "Factor Documents",
      labelPosition: "ABOVE",
      headerCells: {
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Document Name"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "lbl_Associatewith",
            arguments: "Factor / Subfactor"
          ) & " *"
        )
      },
      columnConfigs: {
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: ""),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "")
      },
      rows: #"SYSTEM_SYSRULES_forEach"(
        items: ri!factorDocuments,
        expression: #"SYSTEM_SYSRULES_gridRowLayout"(
          contents: {
            rule!AS_CO_DSP_standardReadOnlyField(value: fv!item.documentName),
            rule!AS_CO_CP_pickerFieldCustom(
              selectedLabels: rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
                criteriaName: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "criteriaId",
                  value: fv!item.criteriaId
                ).criteriaName,
                factorNumber: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "criteriaId",
                  value: fv!item.criteriaId
                ).factorNumber
              ),
              suggestFunction: rule!AS_GSS_UT_suggestFunctionForEvalCriteriaPicker(
                value: _,
                evaluationCriterias: ri!evaluationCriterias
              ),
              value: fv!item.criteriaId,
              saveInto: fv!item.criteriaId,
              required: true,
              maxSelections: 1
            )
          }
        )
      ),
      showWhen: rule!AS_CO_UT_isNotBlank(ri!factorDocuments),
      validations: {},
      shadeAlternateRows: true,
      borderStyle: ""
    ),
    /*Consensus documents*/
    #"SYSTEM_SYSRULES_gridLayout"(
      label: "Consensus Documents",
      labelPosition: "ABOVE",
      headerCells: {
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Document Name"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!i18nData,
            bundleKey: "lbl_Associatewith",
            arguments: "Factor"
          ) & " *"
        )
      },
      columnConfigs: {
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: ""),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "")
      },
      rows: #"SYSTEM_SYSRULES_forEach"(
        items: ri!consensusDocuments,
        expression: #"SYSTEM_SYSRULES_gridRowLayout"(
          contents: {
            rule!AS_CO_DSP_standardReadOnlyField(value: fv!item.documentName),
            rule!AS_CO_CP_pickerFieldCustom(
              selectedLabels: rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
                criteriaName: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "criteriaId",
                  value: fv!item.criteriaId
                ).criteriaName,
                factorNumber: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "criteriaId",
                  value: fv!item.criteriaId
                ).factorNumber
              ),
              suggestFunction: rule!AS_GSS_UT_suggestFunctionForEvalCriteriaPicker(
                value: _,
                evaluationCriterias: /*Filtering only the parent factors*/
                rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "parentCriteriaId",
                  value: tointeger(null)
                )
              ),
              maxSelections: 1,
              value: fv!item.criteriaId,
              saveInto: fv!item.criteriaId,
              required: true
            )
          }
        )
      ),
      showWhen: rule!AS_CO_UT_isNotBlank(ri!consensusDocuments),
      validations: {},
      shadeAlternateRows: true,
      borderStyle: ""
    )
  }
)

