      )
    },
    columnConfigs: {
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: ""),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE", weight: 2),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "")
    },
    rows: #"SYSTEM_SYSRULES_forEach"(
      items: ri!evaluationDocuments,
      expression: #"SYSTEM_SYSRULES_gridRowLayout"(
        contents: {
          rule!AS_CO_DSP_standardReadOnlyField(value: fv!item.documentName),
          rule!AS_CO_INP_paragraphField(
