{
  /* Remove all null-PK factorDocumentMappings associated to currently changed doc */
  a!save(
    ri!factorDocumentMapping,
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!factorDocumentMapping,
      expression: if(
        and(
          tointeger(fv!item.document.appianDocId) = tointeger(ri!vendorDocument.appianDocId),
          rule!AS_CO_UT_isBlank(fv!item.documentMappingId)
        ),
        {},
        fv!item
      )
    )
  ),
  /* Deactivate all previous factorDocumentMappings associated to currently changed doc */
  a!save(
    ri!factorDocumentMapping,
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!factorDocumentMapping,
      expression: if(
        tointeger(
          rule!AS_CO_UT_indexPath(
            cdt: fv!item,
            field: "document.appianDocId",
            indexDefault: null
          )) <> tointeger(ri!vendorDocument.appianDocId),
          fv!item,
          rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: {
              "isActive"
            },
            newValues: {
              false
            }
          )
      )
    )
  ),
  /* Create and append new factorDocumentMappings for each selected factor for the currently changed doc */
  a!save(
    ri!factorDocumentMapping,
    append(
      ri!factorDocumentMapping,
      #"SYSTEM_SYSRULES_forEach"(
        items: save!value,
        expression: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_FactorDocumentMapping'(
          document: ri!vendorDocument,
          factor: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria'(criteriaId: fv!item),
          createdBy: loggedInUser(),
          createdDatetime: now(),
          isActive: true
        )
      )
    )
  )
}

