  rule!AS_CO_UT_rejectNullValuesFromArray(
    array: {
      /*Field Changes Construction for vendor docs*/
      if(
        rule!AS_CO_UT_isBlank(local!groupedDocs.vendorDocs),
        null,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!groupedDocs.vendorDocs,
