data.sail_code:
cast(
  typeof(ri!cdt),
  with(
    local!modifiedBy: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!modifiedBy,
      replacementValue: loggedInUser()
    ),
    local!cdtFields: rule!AS_CO_UT_returnCdtFields(typeOf: ri!cdt),
    local!possibleMetaDataFields: touniformstring(
      /*These fields should always exist in any CDT for which metadata is captured*/
      "modifiedDatetime",
      "modifiedBy",
      "createdDatetime",
      "createdBy",
      /*These fields may not exist in every CDT for which metadata is captured*/
      "isDeleted",
      "isActive"
    ),
    local!capturedMetadataFields: intersection(
      local!cdtFields,
      local!possibleMetaDataFields
    ),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!cdt,
        expression: {
          rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: local!capturedMetadataFields,
            newValues: rule!AS_CO_UT_displayValueMultiple(
              values: local!capturedMetadataFields,
              inArray: local!possibleMetaDataFields,
              replacement: {
                now(),
                local!modifiedBy,
                rule!AS_CO_UT_replaceNull(
                  nullableValue: index(fv!item, "createdDatetime", null),
                  replacementValue: now()
                ),
                rule!AS_CO_UT_replaceNull(
                  nullableValue: index(fv!item, "createdBy", null),
                  replacementValue: local!modifiedBy
                ),
                rule!AS_CO_UT_booleanDefaultFalse(
                  boolean: index(fv!item, "isDeleted", null)
                ),
                rule!AS_CO_UT_booleanDefaultTrue(boolean: index(fv!item, "isActive", null))
              }
            )
          )
        }
      )
    )
  )
)

