      saveInto: ri!saveInto
    ),
    linkStyle: "STANDALONE"
  )
)

a!localVariables(
  local!richTextValue: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: if(
        or(
          rule!AS_CO_UT_isBlank(ri!user),
          rule!AS_CO_UT_isBlank(ri!dateTime),
        ),
        "-",
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_formatDateTime(
              datetime: ri!datetime
            )
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              " ",
              rule!AS_CO_I18N_UT_getAndDisplayLabelSingle(
                bundleFolder: cons!AS_CO_I18N_FLD_INTERNATIONALIZATION_FILES,
                bundleKey: "AS.CO.CommonObjects.lbl_By"
              ),
              " "
            }
          ),
          rule!AS_CO_UT_userLinkDisplay(
            username: ri!user
          )
        }
      )
    ),
    refreshAlways: true
  ),
  if(
    ri!doNotIncludeInComponent,
    local!richTextValue,
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      label: ri!label,
      align: "LEFT",
      labelPosition: rule!AS_CO_UT_getLabelPosition(
        label: ri!label,
        labelPosition: ri!labelPosition,
        readOnly: true
      ),
      value: local!richTextValue
    )
  )
)

if(
  rule!AS_CO_UT_booleanDefaultTrue(ri!showWhen),
