      label: ri!clearSelectionLabel,
      saveInto: ri!saveInto
    ),
    showWhen: rule!AS_CO_UT_isNotBlank(
      ri!value
    )
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    label: ri!label,
    labelPosition: if(
      rule!AS_CO_UT_isBlank(
        ri!label
      ),
      "COLLAPSED",
      "ABOVE"
    ),
    value: #"SYSTEM_SYSRULES_forEach"(
      items: ri!choiceValues,
      expression: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: ri!choiceLabels[fv!index],
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: fv!item,
            saveInto: ri!saveInto,
            showWhen: tostring(
              ri!value
            ) <> tostring(
              fv!item
            )
          ),
          linkStyle: "STANDALONE",
          style: if(
            tostring(
              ri!value
