a!localVariables(
  local!allActiveConsensusResponses: rule!AS_GSS_QR_getConsensusResponses(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    consensusId: ri!currentConsensus[recordType!AS_GSS_ConsensusReport_SYNCEDRECORD.consensusId]
  ),
  /*Inluding only the combined responses and included individual responses to display in the grid*/
  local!consensusResponses: rule!AS_GSS_filterRecords(
    records: local!allActiveConsensusResponses,
    fieldValuePairs: {
      a!map(
        field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.combinedConsensusResponseId,
        value: null
      ),
      a!map(
        field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.isIncluded,
        value: true
      )
    }
  ),
  local!initialMappedResponses: {
    a!map(
      label: "Significant Strengths",
      data: rule!AS_GSS_filterRecords(
        records: local!consensusResponses,
        fieldValuePairs: {
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.responseTypeId,
            value: cons!AS_GSS_REF_ID_RESPONSE_TYPE_STRENGTH
          ),
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.isSignificant,
            value: true
          )
        }
      ),

    ),
    a!map(
      label: "Strengths",
      data: rule!AS_GSS_filterRecords(
        records: local!consensusResponses,
        fieldValuePairs: {
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.responseTypeId,
            value: cons!AS_GSS_REF_ID_RESPONSE_TYPE_STRENGTH
          ),
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.isSignificant,
            value: false
          )
        }
      )
    ),
    a!map(
      label: "Significant Weaknesses",
      data: rule!AS_GSS_filterRecords(
        records: local!consensusResponses,
        fieldValuePairs: {
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.responseTypeId,
            value: cons!AS_GSS_REF_ID_RESPONSE_TYPE_WEAKNESS
          ),
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.isSignificant,
            value: true
          )
        }
      )
    ),
    a!map(
      label: "Weaknesses",
      data: rule!AS_GSS_filterRecords(
        records: local!consensusResponses,
        fieldValuePairs: {
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.responseTypeId,
            value: cons!AS_GSS_REF_ID_RESPONSE_TYPE_WEAKNESS
          ),
          a!map(
            field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.isSignificant,
            value: false
          )
        }
      )
    ),
    a!map(
      label: "Deficiencies",
      data: rule!AS_GSS_filterRecords(
        records: local!consensusResponses,
        field: recordType!AS_GSS_ConsensusResponse_SYNCEDRECORD.responseTypeId,
        value: cons!AS_GSS_REF_ID_RESPONSE_TYPE_DEFICIENCY
      )
    )
  },
  local!mappedResponses: #"SYSTEM_SYSRULES_forEach"(
    items: local!initialMappedResponses,
    expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: fv!item,
      fieldValuePairs: {
        field: "isExpand",
        value: repeat(length({ fv!item.data }), false)
      }
    )
  ),
  {
    /*RATINGS AND SUMMARY*/
    rule!AS_GSS_CPS_ratingsAndSummaryCompleteConsensusReport(
      bundle: ri!bundle,
      currentCriteria: ri!currentCriteria,
      evaluationTasks: ri!evaluationTasks,
      consensusResponse: local!allActiveConsensusResponses,
      currentConsensus: ri!currentConsensus,
      hasMultipleConsensusVersions: ri!hasMultipleConsensusVersions,
      loggedInUser: ri!loggedInUser,
      triggerRefresh: ri!triggerRefresh,
      evaluation: ri!evaluation,
      evaluatorNickNames: ri!evaluatorNickNames
    ),
    /*RESPONSE*/
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      label: "Responses",
      labelSize: "SMALL",
      labelHeadingTag: "H2",
      labelColor: "STANDARD",
      contents: {
        #"SYSTEM_SYSRULES_cardLayout"(
          contents: {
            #"SYSTEM_SYSRULES_forEach"(
              items: local!mappedResponses,
              expression: #"SYSTEM_SYSRULES_sectionLayout_v1"(
                label: fv!item.label,
                labelSize: "SMALL",
                labelColor: "STANDARD",
                contents: rule!AS_GSS_GRD_completeConsensusResponseGrid(
                  currentResponse: fv!item,
                  bundle: ri!bundle
                ),
                divider: if(fv!isLast, "NONE", "BELOW"),
                marginAbove: "STANDARD"
              )
            )
          },
          height: "AUTO",
          style: "NONE",
          shape: "SEMI_ROUNDED",
          padding: "STANDARD",
          marginBelow: "STANDARD",
          showBorder: false,
          showShadow: true
        )
      },
      marginAbove: "STANDARD",
      marginBelow: "NONE"
    )
  }
)

