a!localVariables(
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!evaluationRecord: rule!AS_GSS_QR_getEvaluationByIdentifier(evaluationId: ri!evaluationId),
  local!evaluatorNickNames: rule!AS_GSS_QR_getMaskEvaluatorDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!evaluationId,

  ),
  local!selectedEvaluationFactor: if(
    rule!AS_CO_UT_isBlank(ri!evaluationFactors),
    null,
    ri!evaluationFactors[1]
  ),
  local!subFactorChoices: a!refreshVariable(
    value: if(
      rule!AS_CO_UT_isBlank(local!selectedEvaluationFactor),
      null,
      if(
        rule!AS_CO_UT_isBlank(
          rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluationSubFactors,
            field: "parentCriteriaId",
            value: tointeger(
              local!selectedEvaluationFactor.criteriaId
            )
          )
        ),
        null,
        rule!AS_CO_UT_filterCdtByField(
          cdt: ri!evaluationSubFactors,
          field: "parentCriteriaId",
          value: tointeger(
            local!selectedEvaluationFactor.criteriaId
          )
        )
      )
    ),
    refreshOnReferencedVarChange: true
  ),
  local!selectedEvaluationSubFactor,
  local!evaluationRating: rule!AS_GSS_QE_getRatings(
    parentRatingId: rule!AS_CO_UT_replaceNull(
      nullableValue: index(
        index(
          local!selectedEvaluationSubFactor,
          "ratingType",
          null
        ),
        "ratingId",
        null
      ),
      replacementValue: index(
        index(
          local!selectedEvaluationFactor,
          "ratingType",
          null
        ),
        "ratingId",
        null
      ),

    ),
    executeWhen: or(
      rule!AS_CO_UT_isNotBlank(
        index(
          index(
            local!selectedEvaluationSubFactor,
            "ratingType",
            null
          ),
          "ratingId",
          null
        )
      ),
      rule!AS_CO_UT_isNotBlank(
        index(
          index(
            local!selectedEvaluationFactor,
            "ratingType",
            null
          ),
          "ratingId",
          null
        )
      )
    ),
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  #"SYSTEM_SYSRULES_headerContentLayout"(
    header: "",
    contents: {
      /*Factor and Subfactor dropdown*/
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: {
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_CO_CP_dropdownField(
                  label: "Factor",
                  labelPosition: "ABOVE",
                  placeholderLabel: "Select a Factor",
                  choiceLabels: a!flatten(ri!evaluationFactors).criteriaName,
                  choiceValues: a!flatten(ri!evaluationFactors).criteriaId,
                  value: local!selectedEvaluationFactor.criteriaId,
                  saveInto: {
                    a!save(
                      local!selectedEvaluationFactor,
                      if(
                        rule!AS_CO_UT_isBlank(save!value),
                        null,
                        rule!AS_CO_UT_filterCdtByField(
                          cdt: ri!evaluationFactors,
                          field: "criteriaId",
                          value: save!value
                        )
                      )
                    ),
                    a!save(local!selectedEvaluationSubFactor, null)
                  },
                  /*searchDisplay: "AUTO",*/
                  validations: {}
                )
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_CO_CP_dropdownField(
                  label: "Subfactor",
                  labelPosition: "ABOVE",
                  placeholderLabel: "Select a Subfactor",
                  choiceLabels: if(
                    rule!AS_CO_UT_isBlank(local!subFactorChoices),
                    {},
                    local!subFactorChoices.criteriaName
                  ),
                  choiceValues: if(
                    rule!AS_CO_UT_isBlank(local!subFactorChoices),
                    {},
                    local!subFactorChoices.criteriaId
                  ),
                  value: local!selectedEvaluationSubFactor.criteriaId,
                  saveInto: {
                    a!save(
                      local!selectedEvaluationSubFactor,
                      if(
                        rule!AS_CO_UT_isBlank(save!value),
                        null,
                        rule!AS_CO_UT_filterCdtByField(
                          cdt: local!subFactorChoices,
                          field: "criteriaId",
                          value: save!value
                        )
                      )
                    )
                  },
                  /*searchDisplay: "AUTO",*/
                  disabled: rule!AS_CO_UT_isBlank(local!selectedEvaluationFactor)
                )
              )
            }
          )
        },
        height: "AUTO",
        style: "NONE",
        marginBelow: "STANDARD",
        showBorder: false
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Ratings description and documents*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!AS_GSS_CPS_MethodDescriptionAndRatingDocs(
              evaluationRating: local!evaluationRating,
              selectedEvaluationFactor: rule!AS_CO_UT_replaceNull(
                nullableValue: local!selectedEvaluationSubFactor,
                replacementValue: local!selectedEvaluationFactor
              ),
              i18nData: local!i18nData
            ),
            width: "NARROW_PLUS"
          ),
          /*Vendor Ratings pie chart*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!AS_GSS_CPS_vendorRatingsPerFactor(
              selectedEvaluationFactor: rule!AS_CO_UT_replaceNull(
                nullableValue: local!selectedEvaluationSubFactor,
                replacementValue: local!selectedEvaluationFactor
              ),
              evaluationVendors: ri!evaluationVendors,
              i18nData: local!i18nData,
              evaluation: local!evaluationRecord,
              evaluatorNickNames: local!evaluatorNickNames
            ),
            width: "AUTO"
          )
        }
      )
    },
    backgroundColor: rule!AS_GSS_BrandingValueByKey(
      brandingKey: "DefaultBackground",
      useSuiteBranding: true
    )
  )
)

