a!localVariables(
  local!isColorRating: and(
    rule!AS_CO_UT_isNotBlank(
      ri!selectedEvaluationFactor.refRatingType.refRatingId
    ),
    contains(
      tointeger(
        ri!selectedEvaluationFactor.refRatingType.refRatingId
      ),
      cons!AS_GSS_REF_ID_RATING_TYPE_COLOR_RATING
    )
  ),
  /*Getting unique ratings per user for a vendor (Getting the recent rating from a user*/
  local!uniqueRatingCountPerUser: if(
    rule!AS_CO_UT_isBlank(ri!vendorRatingDetails),
    null,
    #"SYSTEM_SYSRULES_forEach"(
      items: union(
        ri!vendorRatingDetails.userAssignee,
        ri!vendorRatingDetails.userAssignee
      ),
      expression: a!flatten(
        a!localVariables(
          local!userRating: rule!AS_CO_UT_filterCdtByField(
            cdt: ri!vendorRatingDetails,
            field: "userAssignee",
            value: fv!item
          ),
          if(
            length(local!userRating) > 1,
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!userRating,
              field: "taskId",
              value: max(tointeger(local!userRating.taskId))
            ),
            local!userRating
          )
        )
      )
    )
  ),
  /*Constructing a dictionary to be used in the piechart*/
  local!dataForChartSeries: if(
    rule!AS_CO_UT_isBlank(local!uniqueRatingCountPerUser),
    null,
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!childRatingTypes,
      expression: rule!AS_CO_UT_rejectNullValuesFromArray(
        {
          if(
            rule!AS_CO_UT_isBlank(
              rule!AS_CO_UT_filterCdtByField(
                cdt: local!uniqueRatingCountPerUser,
                field: "finalRatingId",
                value: fv!item.ratingId
              )
            ),
            null,
            {
              ratingId: fv!item.ratingId,
              ratinglabel: fv!item.label,
              ratingColor: if(
                local!isColorRating,
                fv!item.color,
                rule!AS_FRM_colorSchemeColorByIndex(
                  colorSchemeValue: rule!AS_GSS_BrandingValueByKey(
                    brandingKey: "ChartColorScheme"
                  ),
                  colorIndex: fv!index
                )
              ),
              user: touniformstring(
                #"SYSTEM_SYSRULES_forEach"(
