        #"SYSTEM_SYSRULES_queryFilter"(
          field: "vendor.vendorId",
          operator: "in",
          value: ri!evaluationVendors.vendorId
        )
      }
    ),
    "data",
    null
  ),
  local!finalRatingVendors: if(
    rule!AS_CO_UT_isBlank(local!finalRatingDetails),
    null,
    tointeger(
      union(
        rule!AS_CO_UT_indexPath(
          cdt: local!finalRatingDetails,
          field: "vendor.vendorId"
        ),
        rule!AS_CO_UT_indexPath(
          cdt: local!finalRatingDetails,
          field: "vendor.vendorId"
        )
      )
    )
  ),
  /*splitting the vendor ratings as three columns*/
  local!thirdIndexVendors: if(
    rule!AS_CO_UT_isBlank(local!finalRatingVendors),
    null,
    tointeger(
      rule!AS_CO_UT_rejectNullValuesFromArray(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!finalRatingVendors,
          expression: if(mod(fv!index, 3) = 0, fv!item, null)
        )
      )
    )
