  local!triggerRefresh,
  local!selectedDocumentId,
  local!searchTerm,
  local!expiryFromDate,
  local!expiryToDate,
  local!createdFromDate,
  local!createdToDate,
  local!businessTypesSearch,
  local!status,
  local!pagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!AS_CO_ENUM_PAGE_SIZE_MEDIUM,
      sort: {
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.pendingUpdateCount,
          ascending: false()
        ),
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
          ascending: true()
        ),
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.sortableTotalPriceValue,
          ascending: true()
        ),
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.submittedDate,
          ascending: false
        ),
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName,
          ascending: true()
        )
      }
    ),
    refreshOnVarChange: {
      local!searchTerm,
      local!expiryFromDate,
      local!expiryToDate,
      local!createdFromDate,
      local!createdToDate,
      local!status,
      local!businessTypesSearch
    },
    refreshAfter:"RECORD_ACTION"
  ),
  local!evaluation: a!refreshVariable(
    value: rule!AS_GSS_QR_getEvaluation(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
      evaluationId: ri!evaluationId,
      fields: {
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingOfficer,
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingSpecialist,
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationChief,
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.createdBy,
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatusId,
        recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId
      }
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  local!assignedContractingTeamUsers: or(
    a!defaultValue(
      value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingOfficer],
      default: ""
    ) = local!loggedInUser,
    a!defaultValue(
      value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingSpecialist],
      default: ""
    ) = local!loggedInUser,
    a!defaultValue(
      value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationChief],
      default: ""
    ) = local!loggedInUser,
    a!defaultValue(
      value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.createdBy],
      default: ""
    ) = local!loggedInUser
  ),
  local!evaluationVendorBusinessTypeVendorIds: a!refreshVariable(
    value: rule!AS_GSS_QR_getEvaluationVendors(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      fields: {
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessTypes.typeCode,
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId,
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionType
      },
      additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessTypes.typeCode,
        operator: "in",
        value: local!businessTypesSearch
      ),
      evaluationId: ri!evaluationId,
      executeWhen: rule!AS_CO_UT_isNotBlank(local!businessTypesSearch)
    )[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId],
    refreshOnReferencedVarChange: true
  ),
  local!criteria: a!refreshVariable(
    value: rule!AS_GSS_QR_getCriteria(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      evaluationId: ri!evaluationId,
      fields: {
        recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaChair
      }
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  local!assignedCriteria: if(
    rule!AS_CO_UT_isBlank(local!criteria),
    null,
    rule!AS_CO_UT_filterRecordByFieldValues(
      records: local!criteria,
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaChair,
      values: local!loggedInUser
    )
  ),
  local!criteriaAssignmentCriteriaIds: a!refreshVariable(
    value: rule!AS_GSS_QR_getCriteriaAssignment(
      fields: {
        recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId
      },
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      criteriaId: local!criteria[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId],
      assignee: local!loggedInUser
    )[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId],
    refreshAfter: "RECORD_ACTION"
  ),
  local!combinedCriteriaIds: if(
    all(
      rule!AS_CO_UT_isBlank(_),
      {
        local!assignedCriteria[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId],
        local!criteriaAssignmentCriteriaIds
      }
    ),
    null,
    union(
      tointeger(
        rule!AS_CO_UT_rejectNullValuesFromArray(
          {
            local!assignedCriteria[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId],
            local!criteriaAssignmentCriteriaIds
          }
        )
      ),
      tointeger({})
    )
  ),
  local!factorDocMapping: if(
    rule!AS_CO_UT_isBlank(local!combinedCriteriaIds),
    null,
    rule!AS_GSS_QR_getFactorDocumentMapping(
      fields: {
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.factorId,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.isActive
      },
      additionalfilters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.factorId,
        operator: "in",
        value: local!combinedCriteriaIds
      ),
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
    )
  ),
  local!mappingEvalDocIds: rule!AS_CO_UT_rejectNullValuesFromArray(
    union(
      index(
        local!factorDocMapping,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
        null
      ),
      index(
        local!factorDocMapping,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
        null
      )
    )
  ),
  local!consensusDetails: a!refreshVariable(
    value: rule!AS_GSS_QR_getConsensusReport(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      fields: recordType!AS_GSS_ConsensusReport_SYNCEDRECORD.consensusId,
      additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_ConsensusReport_SYNCEDRECORD.evaluationId,
        operator: "=",
        value: ri!evaluationId
      ),
      executeWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId)
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  local!consensusDocs: a!refreshVariable(
    value: rule!AS_GSS_QR_getEvaluationDocuments(
      evaluationId: ri!evaluationId,
      fields: {
        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.criteriaId
      },
      filters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.consensusId,
        operator: "in",
        value: tointeger(
          local!consensusDetails[recordType!AS_GSS_ConsensusReport_SYNCEDRECORD.consensusId]
        )
      ),
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      executeWhen: rule!AS_CO_UT_isNotBlank(
        local!consensusDetails[recordType!AS_GSS_ConsensusReport_SYNCEDRECORD.consensusId]
      )
    ),
    refreshOnReferencedVarChange: true
  ),
  local!visibleConsensusDocIds: if(
    rule!AS_CO_UT_isUserMemberOfGroups(
      groups: cons!AS_GSS_GRP_FACTOR_ADVISOR,
      user: local!loggedInUser
    ),
    index(
      local!consensusDocs,
      recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
      null
    ),
    if(
      rule!AS_CO_UT_isBlank(local!combinedCriteriaIds),
      null,
      index(
        rule!AS_CO_UT_filterRecordByFieldValues(
          records: local!consensusDocs,
          field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.criteriaId,
          values: local!combinedCriteriaIds
        ),
        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
        null
      )
    )
  ),
  local!evaluationVendors: a!refreshVariable(
    value: rule!AS_GSS_QR_getEvaluationVendors(
      evaluationId: ri!evaluationId,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      fields: {
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessTypes,
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.businessTypes
      },
      triggerRefresh: local!triggerRefresh,
      additionalFilters: /*filtering only searched businesstypes*/
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.evaluationId,
        operator: "<>",
        value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
        applywhen: and(
          rule!AS_CO_UT_isNotBlank(local!businessTypesSearch),
          rule!AS_CO_UT_isBlank(
            local!evaluationVendorBusinessTypeVendorIds
          )
        )
      ),
      logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        logicalExpressions: {
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.status,
                operator: "in",
                value: local!status,
                applyWhen: rule!AS_CO_UT_isNotBlank(local!status)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.expirationDate,
                operator: ">=",
                value: local!expiryFromDate,
                applyWhen: rule!AS_CO_UT_isNotBlank(local!expiryFromDate)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.expirationDate,
                operator: "<=",
                value: local!expiryToDate,
                applyWhen: rule!AS_CO_UT_isNotBlank(local!expiryToDate)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.submittedDate,
                operator: ">=",
                value: rule!AS_GSS_UT_formatDateToDateTimeForFilters(
                  date: local!createdFromDate,
                  isStartDate: true()
                ),
                applyWhen: rule!AS_CO_UT_isNotBlank(local!createdFromDate)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.submittedDate,
                operator: "<=",
                value: rule!AS_GSS_UT_formatDateToDateTimeForFilters(
                  date: local!createdToDate,
                  isStartDate: false()
                ),
                applyWhen: rule!AS_CO_UT_isNotBlank(local!createdToDate)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId,
                operator: "in",
                value: local!evaluationVendorBusinessTypeVendorIds,
                applywhen: rule!AS_CO_UT_isNotBlank(local!businessTypesSearch)
              )
            },
            ignoreFiltersWithEmptyValues: true
          ),
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            ignoreFiltersWithEmptyValues: true,
            operator: "OR",
            filters: {
              /*TIN, FEIN, UEI, CAGE*/
              #"SYSTEM_SYSRULES_queryFilter"(
                field: if(
                  local!isStateOrLocal,
                  recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.stateAndLocalIdentifier,
                  recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.federalIdentifier
                ),
                operator: "includes",
                value: local!searchTerm,
                applyWhen: rule!AS_CO_UT_isNotBlank(local!searchTerm)
              ),
              /*Name*/
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName,
                operator: "includes",
                value: local!searchTerm,
                applyWhen: rule!AS_CO_UT_isNotBlank(local!searchTerm)
              )
            }
          )
        },
        ignoreFiltersWithEmptyValues: true
      ),
      executeWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId),
      pagingInfo: local!pagingInfo
    ),
    refreshOnReferencedVarChange: true,
    refreshAfter: "RECORD_ACTION"
  ),
  local!vendorDocs: a!refreshVariable(
    value: rule!AS_GSS_QR_getEvaluationDocuments(
      executeWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId),
      evaluationId: ri!evaluationId,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorId,
          operator: "not null"
        ),

      },
      logicalExpression: {
        /*Query logical expression to filter documents based on user's access*/
        if(
          local!assignedContractingTeamUsers,
          null,
          if(
            all(
              rule!AS_CO_UT_isBlank(_),
              {
                local!combinedCriteriaIds,
                local!mappingEvalDocIds,
                local!visibleConsensusDocIds
              }
            ),
            /*If the user doesn't have any association with the evaluation, he will not see any documents*/
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              ignoreFiltersWithEmptyValues: true,
              operator: "AND",
              filters: #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationId,
                operator: "<>",
                value: local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
              )
            ),
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              ignoreFiltersWithEmptyValues: true,
              operator: "OR",
              logicalexpressions: {
                #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  ignoreFiltersWithEmptyValues: true,
                  operator: "OR",
                  filters: {
                    /*Consensus report docs*/
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
                      operator: "in",
                      value: tointeger(local!visibleConsensusDocIds),
                      applywhen: rule!AS_CO_UT_isNotBlank(local!visibleConsensusDocIds)
                    ),
                    /* Getting Criteria chair's criteria docs and
                       Evaluator's Criteria assignment documents*/
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
                      operator: "in",
                      value: tointeger(local!mappingEvalDocIds),
                      applywhen: and(
                        rule!AS_CO_UT_isNotBlank(local!mappingEvalDocIds),
                        rule!AS_CO_UT_isNotBlank(local!combinedCriteriaIds)
                      )
                    )
                  }
                )
              }
            )
          )
        )
      },
      triggerRefresh: local!triggerRefresh
    ),
    refreshOnReferencedVarChange: true,
    refreshAfter: "RECORD_ACTION"
  ),
  local!showPricingInfo: or(
    local!assignedContractingTeamUsers,
    contains(
      touniformstring(
        index(
          local!criteria,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaChair,
          null
        )
      ),
      tostring(local!loggedInUser)
    ),
    rule!AS_CO_UT_isUserMemberOfGroups(
      groups: cons!AS_GSS_GRP_FACTOR_ADVISOR,
      user: local!loggedInUser
    )
  ),
  local!summaryLinks: rule!AS_GSS_UT_retrieveSummaryLinksForVendorsFromSuite(
    isStateOrLocal: local!isStateOrLocal,
    evaluationVendors: local!evaluationVendors
  ),
  rule!AS_GSS_HCL_vendorsTab(
    contents: if(
      rule!AS_CO_UT_isBlank(local!selectedDocumentId),
      {
        rule!AS_GSS_FM_evaluationVendorsFilters(
          searchTerm: local!searchTerm,
          expiryFromDate: local!expiryFromDate,
          expiryToDate: local!expiryToDate,
          createdFromDate: local!createdFromDate,
          createdToDate: local!createdToDate,
          status: local!status,
          i18Data: local!i18nData,
          businessTypes: local!businessTypes,
          businessTypesSearch: local!businessTypesSearch,
          isStateOrLocal: local!isStateOrLocal
        ),
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: {
            #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {})
          }
        ),
        rule!AS_GSS_GRD_EvaluationVendors(
          pagingInfo: local!pagingInfo,
          evaluationVendors: local!evaluationVendors,
          i18nData: local!i18nData,
          vendorDocuments: local!vendorDocs,
          selectedDocumentId: local!selectedDocumentId,
          isStateOrLocal: local!isStateOrLocal,
          showPricingInfo: local!showPricingInfo,
          isSetUpPending: contains(
            {
              cons!AS_GSS_REF_ID_EVALUATION_STATUS_SETTING_UP
            },
            tointeger(
              local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatusId]
            )
          ),
          isLptaEvaluation: tointeger(
            local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
          ) = cons!AS_GSS_REF_ID_EVALUATION_METHOD_LPTA,
          summaryLinks: local!summaryLinks
        )
      },
      {
        rule!AS_CO_DSP_backLink(
          label: "Go back to vendors",
          saveInto: {
            rule!AS_CO_UT_saveNull(valueToNull: local!selectedDocumentId)
          }
        ),
        rule!AS_GSS_CPS_selectedEvaluationDocumentView(
          selectedDocumentId: local!selectedDocumentId,
          i18nData: local!i18nData
        )
      }
    )
  )
)

