            bundleKey: "lbl_XWeightUnitShortForm",
            arguments: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
          )
        ),
        width: "ICON_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Chair",
        value: rule!AS_GSS_getUserFullName(
          fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaChair]
        ),
        width: "ICON_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!i18nData,
          bundleKey: "Description"
        ),
        value: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaDescription],
        width: "NARROW_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Rating",
        value: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating.refLabel],
        width: "ICON_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Team",
        value: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.EvaluatorTeam.teamName],
        width: "NARROW"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Evaluators",
        value: rule!AS_GSS_UT_returnUserOrNickName(
          userNames: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment.assignee],
          isMaskedEvaluation: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
          nickNamesList: ri!evaluatorNicknames
        ),
        width: "NARROW_PLUS"
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        value: if(
          a!isNullOrEmpty(
            rule!AS_CO_UT_filterRecordByFieldValues(
              records: ri!evaluationSubFactors,
              field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
              values: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
            )
          ),
          null,
          if(
            contains(
              ri!expandedFactorIds,
              fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
            ),
            /* Collapse */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "compress-corners",
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    saveInto: {
                      a!save(
                        ri!expandedFactorIds,
                        rule!AS_CO_UT_rejectValuesFromArray(
                          array: ri!expandedFactorIds,
                          values: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                        )
                      )
                    }
                  ),
                  linkStyle: "STANDALONE",
                  caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: "cpt_CollapseSubfactors",
                    arguments: {
                      fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName]
                    }
                  )
                )
              },
              marginAbove: "NONE",
              marginBelow: "NONE"
            ),
            /* Expand */
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "expand-alt",
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    saveInto: {
                      a!save(
                        ri!expandedFactorIds,
                        append(
                          ri!expandedFactorIds,
                          fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                        )
                      )
                    }
                  ),
                  linkStyle: "STANDALONE",
                  caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: "cpt_ExpandSubfactors",
                    arguments: {
                      fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName]
                    }
                  )
                )
              },
              marginAbove: "NONE",
              marginBelow: "NONE"
            )
          )
        ),
        width: "ICON"
      )
    }
  )
)

