data.sail_code:
a!localVariables(
  local!loggedInUser: loggedInUser(),
  local!refreshAfterRecordAction: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION",
    refreshOnVarChange: ri!triggerRefresh
  ),
  local!evaluation: rule!AS_GSS_QE_getEvaluation(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    evaluationId: ri!evaluationId
  ),
  local!assignedContractingTeamUsers: or(
    local!evaluation.contractingOfficer.username = local!loggedInUser,
    local!evaluation.contractingSpecialist.username = local!loggedInUser,
    local!evaluation.evaluationChief.username = local!loggedInUser,
    local!evaluation.createdBy = local!loggedInUser
  ),
  local!criteria: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!evaluationId
  ),
  local!assignedCriteria: if(
    rule!AS_CO_UT_isBlank(local!criteria),
    null,
    if(
      local!assignedContractingTeamUsers,
      local!criteria,
      rule!AS_CO_UT_filterCdtByField(
        cdt: local!criteria,
        field: "criteriaChair.username",
        value: local!loggedInUser
      )
    )
  ),
  local!criteriaAssignmentCriteriaIds: rule!AS_GSS_QR_getCriteriaAssignment(
    fields: {
      recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId
    },
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    executeWhen: rule!AS_CO_UT_isNotBlank(local!criteria.criteriaId),
    criteriaId: local!criteria.criteriaId,
    assignee: local!loggedInUser
  )[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId],
  local!combinedCriteriaIds: if(
    all(
      rule!AS_CO_UT_isBlank(_),
      {
        local!assignedCriteria.criteriaId,
        local!criteriaAssignmentCriteriaIds
      }
    ),
    null,
    union(
      tointeger(
        rule!AS_CO_UT_rejectNullValuesFromArray(
          {
            local!assignedCriteria.criteriaId,
            local!criteriaAssignmentCriteriaIds
          }
        )
      ),
      tointeger({})
    )
  ),
  local!factorDocMapping: rule!AS_GSS_QR_getFactorDocumentMapping(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    fields: {
      recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.criteria.criteriaName,
      recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.criteria.factorNumber,
      recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId
    },
    additionalfilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.factorId,
      operator: "in",
      value: local!combinedCriteriaIds
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      1,
      cons!AS_GSS_INT_QUERY_RECORD_MAX_BATCH_SIZE
    ),
    triggerRefresh: local!refreshAfterRecordAction,
    executeWhen: rule!AS_CO_UT_isNotBlank(local!combinedCriteriaIds)
  ).data,
  local!mappingEvalDocIds: rule!AS_CO_UT_rejectNullValuesFromArray(
    union(
      index(
        local!factorDocMapping,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
        null
      ),
      index(
        local!factorDocMapping,
        recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
        null
      )
    )
  ),
  local!consensusDetails: rule!AS_GSS_QE_getConsensusReport(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: "evaluationId",
      operator: "=",
      value: ri!evaluationId
    ),
    executeWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId)
  ),
  local!consensusDocs: rule!AS_GSS_QE_getEvaluationDocuments(
    evaluationId: ri!evaluationId,
    filters: #"SYSTEM_SYSRULES_queryFilter"(
      field: "consensusId",
      operator: "in",
      value: tointeger(local!consensusDetails.consensusId)
    ),
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    executeWhen: rule!AS_CO_UT_isNotBlank(local!consensusDetails.consensusId),
    triggerRefresh: local!refreshAfterRecordAction
  ),
  local!visibleConsensusDocIds: if(
    rule!AS_CO_UT_isUserMemberOfGroups(
      groups: cons!AS_GSS_GRP_FACTOR_ADVISOR,
      user: local!loggedInUser
    ),
    index(
      local!consensusDocs,
      "evalDocumentId",
      null
    ),
    if(
      rule!AS_CO_UT_isBlank(local!combinedCriteriaIds),
      null,
      index(
        rule!AS_CO_UT_filterCdtByField(
          cdt: local!consensusDocs,
          field: "criteriaId",
          value: local!combinedCriteriaIds
        ),
        "evalDocumentId",
        null
      )
    )
  ),
  #"SYSTEM_SYSRULES_gridField_v2"(
    label: "Documents",
    labelPosition: "COLLAPSED",
    emptyGridMessage: "No documents available",
    data: rule!AS_GSS_QE_getEvaluationDocAdditionalInfo(
      evaluationId: ri!evaluationId,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      pagingInfo: fv!pagingInfo,
      triggerRefresh: local!refreshAfterRecordAction,
      logicalExpression: {
        rule!AS_CO_UT_createSearchLogicalExpression(
          searchTerm: ri!searchTerm,
          fields: {
            "documentName",
            "documentDescription",
            "docTypeLabel",
            "legalName",
            "criteriaName",
            "factorNumber"
          }
        ),
        /*Query logical expression for selected associationIds (vendorId / criteriaId)*/
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          ignoreFiltersWithEmptyValues: true,
          operator: "OR",
          filters: {
            if(
              rule!AS_CO_UT_isBlank(ri!selectedAssociationId),
              {},
              {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "criteriaId",
                  operator: "not null",
                  applyWhen: contains(
                    ri!selectedAssociationId,
                    cons!AS_GSS_REF_ID_DOC_ASSOCIATION_FACTOR
                  )
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "evalDocumentId",
                  operator: "in",
                  value: local!mappingEvalDocIds,
                  applyWhen: contains(
                    ri!selectedAssociationId,
                    cons!AS_GSS_REF_ID_DOC_ASSOCIATION_FACTOR
                  )
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "vendorId",
                  operator: "not null",
                  applyWhen: contains(
                    ri!selectedAssociationId,
                    cons!AS_GSS_REF_ID_DOC_ASSOCIATION_VENDOR
                  ),

                )
              }
            )
          },
          logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            ignoreFiltersWithEmptyValues: true,
            operator: "AND",
            filters: {
              if(
                rule!AS_CO_UT_isBlank(ri!selectedAssociationId),
                {},
                {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "criteriaId",
                    operator: "is null",
                    applyWhen: contains(
                      ri!selectedAssociationId,
                      cons!AS_GSS_REF_ID_DOC_ASSOCIATION_EVALUATION
                    )
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "vendorId",
                    operator: "is null",
                    applyWhen: contains(
                      ri!selectedAssociationId,
                      cons!AS_GSS_REF_ID_DOC_ASSOCIATION_EVALUATION
                    )
                  )
                }
              )
            }
          )
        ),
        /*Query logical expression to filter documents based on user's access*/
        if(
          local!assignedContractingTeamUsers,
          null,
          /*If the user doesn't have any association with the evaluation, they will only see reference documents*/
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            ignoreFiltersWithEmptyValues: true,
            operator: "OR",
            /* all users who can see the evaluation can see evaluation reference docs */
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: "docType",
              operator: "=",
              value: cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
            ),
            logicalExpressions: if(
              all(
                rule!AS_CO_UT_isBlank(_),
                {
                  local!combinedCriteriaIds,
                  local!mappingEvalDocIds,
                  local!visibleConsensusDocIds
                }
              ),
              {},
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                ignoreFiltersWithEmptyValues: true,
                operator: "OR",
                logicalexpressions: {
                  #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    ignoreFiltersWithEmptyValues: true,
                    operator: "OR",
                    filters: {
                      /*Consensus report docs*/
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: "evalDocumentId",
                        operator: "in",
                        value: tointeger(local!visibleConsensusDocIds),
                        applywhen: rule!AS_CO_UT_isNotBlank(local!visibleConsensusDocIds)
                      ),
                      /* Getting Criteria chair's criteria docs and
                       Evaluator's Criteria assignment documents*/
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: "criteriaId",
                        operator: "in",
                        value: tointeger(local!combinedCriteriaIds),
                        applywhen: rule!AS_CO_UT_isNotBlank(local!combinedCriteriaIds)
                      ),
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: "evalDocumentId",
                        operator: "in",
                        value: tointeger(local!mappingEvalDocIds),
                        applywhen: and(
                          rule!AS_CO_UT_isNotBlank(local!mappingEvalDocIds),
                          rule!AS_CO_UT_isNotBlank(local!combinedCriteriaIds)
                        )
                      ),

                    }
                  ),
                  /*Query logical expression for evaluator type documents - Uploaded by an evaluator*/
                  if(
                    and(
                      rule!AS_CO_UT_isUserMemberOfGroups(
                        groups: cons!AS_GSS_GRP_EVALUATORS,
                        user: local!loggedInUser
                      ),
                      rule!AS_CO_UT_isNotBlank(local!criteriaAssignmentCriteriaIds)
                    ),
                    #"SYSTEM_SYSRULES_queryLogicalExpression"(
                      ignoreFiltersWithEmptyValues: true,
                      operator: "AND",
                      filters: {
                        #"SYSTEM_SYSRULES_queryFilter"(
                          field: "createdBy",
                          operator: "=",
                          value: local!loggedInUser
                        ),
                        #"SYSTEM_SYSRULES_queryFilter"(
                          field: "docType",
                          operator: "=",
                          value: cons!AS_GSS_REF_ID_DOC_TYPE_EVALUATOR
                        )
                      }
                    ),
                    null
                  )
                }
              )
            )
          )
        )
      }
    ),
    columns: {
      /*Document Name*/
      rule!AS_GSS_UI_docGridColumn_name(
        docIdFieldName: "appianDocId",
        docNameFieldName: "documentName",
        documentIdFieldName: "evalDocumentId",
        docExtensionFieldName: "fileType",
        docVersionFieldName: "version",
        docSourceFieldName: "sourceApplicationId",
        selectedDocumentId: ri!selectedDocumentId,
        i18nData: ri!i18nData,
        bundleKey: "lbl_DocName"
      ),
      /*Description*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Description",
        value: rule!AS_CO_UT_replaceNull(
          nullableValue: fv!row.documentDescription,
          replacementValue: "-"
        )
      ),
      /*Document type*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Document Type",
        value: rule!AS_CO_UT_replaceNull(
          nullableValue: fv!row.docTypeLabel,
          replacementValue: "-"
        )
      ),
      /*Associated With vendors*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Associated With Vendor",
        value: if(
          rule!AS_CO_UT_isNotBlank(fv!row.vendorId),
          fv!row.legalName,
          "-"
        )
      ),
      /*Associated With factor/subfactor(s)*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Associated With Factor/Subfactor(s)",
        value: if(
          rule!AS_CO_UT_isNotBlank(fv!row.criteriaId),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: #"SYSTEM_SYSRULES_richTextBulletedList"(
              items: rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
                criteriaName: fv!row.criteriaName,
                factorNumber: fv!row.factorNumber
              )
            )
          ),
          if(
            rule!AS_CO_UT_isNotBlank(fv!row.vendorId),
            a!localVariables(
              local!factorDetails: rule!AS_CO_UT_filterRecordByFieldValues(
                records: local!factorDocMapping,
                field: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId,
                values: fv!row.evalDocumentId
              ),
              if(
                rule!AS_CO_UT_isNotBlank(local!factorDetails),
                rule!AS_GSS_CPS_richTextListWithShowMoreLess(
                  items: #"SYSTEM_SYSRULES_forEach"(
                    local!factorDetails,
                    rule!AS_GSS_UT_appendFactorNumberAndCriteriaName(
                      criteriaName: fv!item[recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.criteria.criteriaName],
                      factorNumber: fv!item[recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.criteria.factorNumber]
                    ) & char(10)
                  ),
                  shortenIfLongerThanCount: cons!AS_GAM_INT_LIMIT_FOR_ASSOCIATE_WITH_FACTORS,
                  i18nData: ri!i18nData,
                  showBulletedList: true(),
                  includeLineBreak: false()
                ),
                "-"
              )
            ),
            "-"
          )
        )
      ),
      /*Uploaded on*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Uploaded On",
        sortField: "createdDatetime",
        align: "END",
        value: rule!AS_CO_UT_replaceNull(
          nullableValue: rule!AS_CO_UT_formatDate(
            date: rule!AS_CO_UT_convertDateTimeToDate(dateTime: fv!row.createdDatetime)
          ),
          replacementValue: "-"
        )
      ),
      /*Document download link*/
      #"SYSTEM_SYSRULES_gridColumn"(
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(
              altText: "Download file",
              icon: "download",
              size: "MEDIUM",
              caption: "Press enter to download the document",
              link: rule!AS_GSS_CO_documentDownloadLink(
                document: fv!row.appianDocId,
                sourceApplicationId: fv!row.sourceApplicationId
              ),
              linkstyle: "STANDALONE"
            )
          }
        ),
        width: "ICON"
      ),
      /* Doc review RA */
      #"SYSTEM_SYSRULES_gridColumn"(
        value: #"SYSTEM_SYSRULES_recordActionField_v1"(
          actions: rule!AS_GSS_RA_submitDocForReviewRecordActionItem(docId: fv!row.evalDocumentId),
          style: "LINKS",
          accessibilityText: "Click here to submit document for review",
          display: "ICON",
          openActionsIn: "DIALOG",
          align: "START",

        ),
        width: "ICON"
      ),
      /* Doc delete RA*/
      #"SYSTEM_SYSRULES_gridColumn"(
        showWhen: contains(
          {
            cons!AS_GSS_REF_ID_EVALUATION_STATUS_SETTING_UP,
            cons!AS_GSS_REF_ID_EVALUATION_STATUS_INPROGRESS
          },
          tointeger(
            local!evaluation.evaluationStatus.refDataId
          )
        ),
        width: "ICON",
        value: #"SYSTEM_SYSRULES_recordActionField_v1"(
          actions: rule!AS_GSS_RA_deleteEvaluationDocumentRecordActionItem(
            evaluationDocumentId: fv!row.evalDocumentId
          ),
          style: "LINKS",
          accessibilityText: "Click to delete",
          display: "ICON",
          openActionsIn: "DIALOG",
          align: "START"
        )
      )
    },
    pageSize: cons!AS_CO_ENUM_PAGE_SIZE_MEDIUM,
    initialSorts: #"SYSTEM_SYSRULES_sortInfo"(field: "createdDatetime"),
    shadeAlternateRows: true,
    borderStyle: "LIGHT"
  )
)

a!localVariables(
  local!isEvaluatorMasked: a!defaultValue(
    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
    false()
  ),
  #"SYSTEM_SYSRULES_gridField_v2"(
    label: ri!gridLabel,
    accessibilityText: "After selecting a record, controls for interacting with the record are positioned directly before this grid.",
    emptyGridMessage: "No open tasks",
    data: #"SYSTEM_SYSRULES_recordData"(
      recordType: recordType!AS_GSS_TMG_Task_SYNCEDRECORD,
      fields: {
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskName,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.completionDate,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorDisplayName,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.dueDate,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.configurationLevelCode,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskCategory.categoryName,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluationId,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationNumber,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationTitle,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationStatus.refDataId,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.EvaluatorTeam.teamName,
        recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria
      },
      filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId,
            operator: "in",
            value: {
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
            }
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluationId,
            operator: "=",
            value: ri!evaluationId,
            applyWhen: rule!AS_CO_UT_isNotBlank(ri!evaluationId)
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode,
            operator: "<>",
            value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_AUTOMATED
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode,
            operator: "=",
            value: cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION
          ),
          if(
            ri!isFactor,
            {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.parentCriteriaId,
                operator: "is null"
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.criteriaId,
                operator: "=",
                value: ri!selectedFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
              )
            },
            {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.parentCriteriaId,
                operator: "not null"
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.criteriaId,
                operator: "=",
                value: ri!selectedSubfactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
              )
            }
          ),

        },
        ignoreFiltersWithEmptyValues: true()
      )
    ),
    columns: {
      /*task name*/
      #"SYSTEM_SYSRULES_gridColumn"(
        width: "MEDIUM",
        label: "Task",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskName],
              link: #"SYSTEM_SYSRULES_startProcessLink"(
                processModel: cons!AS_GSS_PM_COMPLETE_EVALUATION,
                processParameters: {
                  task: rule!AS_GSS_TMG_QE_getRuntimeTask(
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    taskId: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId]
                  )
                },
                showWhen: if(
                  /*disable task actions*/
                  or(
                    rule!AS_CO_UT_isBlank(fv!row),
                    #"SYSTEM_SYSRULES_forEach"(
                      items: fv!row,
                      expression: if(
                        /*First check that the task status is assigned*/
                        rule!AS_CO_UT_not(
                          contains(
                            {
                              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
                            },
                            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
                          )
                        ),
                        true,
                        if(
                          /*Then check if there is a default assignee on the task*/
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
                          ),
                          /*Return false (not disabled) if EITHER the user is in the default assignee group OR the user is the individual assignee*/
                          /*Returns true (disabled) when the task behaviour type is "Complete Evaluation" and the loggedInUser is not a userAssignee*/
                          and(
                            or(
                              tostring(
                                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
                              ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
                              rule!AS_CO_UT_not(
                                rule!AS_CO_UT_isUserMemberOfGroups(
                                  groups: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee],
                                  user: loggedInUser()
                                )
                              )
                            ),
                            rule!AS_CO_UT_not(
                              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = loggedInUser()
                            )
                          ),
                          /*Return false (not disabled) if the user is the individual assignee*/
                          rule!AS_CO_UT_not(
                            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = loggedInUser()
                          )
                        )
                      )
                    )
                  ),
                  false,
                  /*check if has ui form*/
                  contains(
                    {
                      cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_CHECKBOX,
                      cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_DATA_ENTRY
                    },
                    fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode]
                  )
                )
              ),
              linkStyle: "STANDALONE",
              style: "STRONG"
            )
          }
        )
      ),
      /*type*/
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Type",
        align: "START",
        value: rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!i18nData,
          bundleKey: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorDisplayName],

        ),
        width: "MEDIUM"
      ),
      /*assignee*/
      #"SYSTEM_SYSRULES_gridColumn"(
        width: "NARROW_PLUS",
        label: "Assignee",
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          preventWrapping: true,
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: if(
                rule!AS_CO_UT_isBlank(
                  fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
                ),
                cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT,
                if(
                  local!isEvaluatorMasked,
                  rule!AS_GSS_UT_returnNickName(
                    userNames: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee],
                    nickNamesList: ri!evaluatorNickNames
                  ),
                  rule!AS_CO_UT_userLinkDisplay(
                    username: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
                  )
                )
              )
            )
          }
        )
      ),
      /*due date*/
      rule!AS_GSS_TMG_UI_myTasksGrid_colDueDate(
        i18nData: ri!i18nData,
        loggedInUser: loggedInUser(),
        width: "NARROW_PLUS"
      )
    },
    selectable: true,
    selectionvalue: if(
      ri!isFactor,
      a!flatten(ri!factorSelectedTasks)[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId],
      a!flatten(ri!subfactorSelectedTasks)[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskId]
    ),
    selectionsaveinto: if(
      ri!isFactor,
      rule!AS_CO_UT_simpleGridSelectionSaveInto(target: ri!factorSelectedTasks),
      rule!AS_CO_UT_simpleGridSelectionSaveInto(target: ri!subfactorSelectedTasks)
    ),
    initialSorts: #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.dueDate,
      ascending: true
    ),
    pageSize: cons!AS_CO_ENUM_PAGE_SIZE_MEDIUM,
    borderStyle: "LIGHT",
    userFilters: #"urn:appian:user-filter:v1:9a04b944-b726-41f5-9b37-8ec71b6cc370/e503a214-f57a-48a7-9c99-2c3dcf7c7eac",
    refreshOnVarChange: ri!triggerRefresh
  )
)

