        or(
          a!isNullOrEmpty(
            fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
          ),
          rule!AS_GSS_ShowFactorWeight_wrapper(
            loggedInUser: ri!loggedInUser,
            allFactors: ri!allFactors,
            currentFactor: fv!row,
            evaluation: ri!evaluation
          ) = false
        ),
        "-",
        rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!i18nData,
          bundleKey: "lbl_XWeightUnitShortForm",
          arguments: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
