      width: "NARROW_PLUS"
    ),
    /*evaluation team*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Evaluation Team",
      value: if(
        rule!AS_CO_UT_isNotBlank(
          fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluatorTeamId]
