        fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.EvaluatorTeam.teamName],
        "-"
      )
    ),
    /*evaluators*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Evaluators",
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        label: "Evaluators",
        value: if(
          rule!AS_CO_UT_isNotBlank(
            rule!AS_CO_UT_filterRecordByFieldValues(
              records: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment],
              field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
              values: true
            )
          ),
          {
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterRecordByFieldValues(
                records: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment],
                field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
                values: true
              ),
              expression: if(
                fv!isLast,
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: if(
                    rule!AS_CO_UT_isBlank(
                      fv!item[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.assignee]
                    ),
                    "-",
                    rule!AS_GSS_UT_returnUserOrNickName(
                      userNames: fv!item[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.assignee],
                      isMaskedEvaluation: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
                      nickNamesList: ri!evaluatorNickNames
                    )

                  ),
                  color: "STANDARD",
                  size: "STANDARD"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    if(
                      rule!AS_CO_UT_isBlank(
                        fv!item[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.assignee]
                      ),
                      "-",
                      rule!AS_GSS_UT_returnUserOrNickName(
                        userNames: fv!item[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.assignee],
                        isMaskedEvaluation: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
                        nickNamesList: ri!evaluatorNickNames
                      )

                    ) & ", "
                  },
                  color: "STANDARD",
                  size: "STANDARD"
                )
              )
            )
          },
          "-"
        )
      )
    ),
    /*factor rating*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Rating",
      sortField: recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating.refLabel,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_replaceNull(
              nullableValue: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating.refLabel],
              replacementValue: "-"
            )
          )
        }
      )
    ),
    /*duedate*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Due Date",
      sortField: recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate,
      value: if(
        rule!AS_CO_UT_isBlank(
          fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
        ),
        cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT,
        if(
          cons!AS_GSS_TMG_BOL_BUSINESS_DAYS_ONLY,
          /*Business days*/
          a!localVariables(
            /*Extracting date from dueDate of type dateTime*/
            local!dueDate: todate(
              fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
            ),
            /*Getting today based on user's timezone*/
            local!today: rule!AS_CO_UT_returnTodayBasedOnInput(username: ri!loggedInUser),
            /*calculating the end date diff from today*/
            local!workingDays: calworkdays(local!today, local!dueDate),
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              preventWrapping: true,
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "exclamation-triangle",
                  size: "SMALL",
                  caption: "Overdue",
                  color: if(local!workingDays < 0, "NEGATIVE", "#FFF"),
                  showWhen: not(
                    fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaStatusId] = cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " ",
                  showWhen: not(
                    fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaStatusId] = cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: rule!AS_CO_UT_formatDate(
                    date: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                  ),
                  color: "STANDARD"
                )
              }
            )
          ),
          /*Not Business days*/
          a!localVariables(
            /*Extracting date from dueDate of type dateTime*/
            local!dueDate: todate(
              fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
            ),
            /*Getting today based on user's timezone*/
            local!today: rule!AS_CO_UT_returnTodayBasedOnInput(username: ri!loggedInUser),
            /*calculating the end date diff from today*/
            /*checking if alreadt expired*/
            local!expired: local!dueDate < local!today,
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              preventWrapping: true,
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "exclamation-triangle",
                  size: "SMALL",
                  caption: "Overdue",
                  color: if(local!expired, "NEGATIVE", "#FFF"),
                  showWhen: not(
                    fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaStatusId] = cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " ",
                  showWhen: not(
                    fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaStatusId] = cons!AS_GSS_REF_ID_CRITERIA_STATUS_COMPLETED
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: rule!AS_CO_UT_formatDate(
                    date: fv!row[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                  ),
                  color: "STANDARD"
                )
              }
            )
          )
        )
      ),
      align: "END"
    )
  },
  pageSize: 5,
  selectable: false,
  spacing: "STANDARD",
  height: "AUTO",
  borderStyle: "LIGHT"
)

