            taskStatuses: ri!taskStatuses,
            isParent: true,
            expand: local!expand,
            showInitiateTaskColumn: local!showInitiateTaskColumn,
            isEvaluatorMasked: ri!isEvaluatorMasked,
            evaluatorNickNames: ri!evaluatorNickNames
          ),
          if(
            local!expand,
            #"SYSTEM_SYSRULES_forEach"(
              items: fv!item.taskPrecedents,
              expression: a!localVariables(
                local!task: rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!tasksForOnboarding,
                  field: "taskId",
                  value: fv!item.taskId_precedent
                ),
                rule!AS_GSS_TMG_UI_OutstandingTaskGridRow(
                  i18nData: ri!i18nData,
                  taskStatuses: ri!taskStatuses,
                  isParent: false,
                  precedingTask: local!task,
                  showInitiateTaskColumn: local!showInitiateTaskColumn,
                  isEvaluatorMasked: ri!isEvaluatorMasked,
                  evaluatorNickNames: ri!evaluatorNickNames
                )
              )
            ),
            {}
          )
        }
      )
    ),
    spacing: "DENSE",
    rowHeader: 1
  )
)

a!localVariables(
  local!showReopenTaskColumn: rule!AS_GSS_TMG_BL_reopenTasksVisibility(),
  #"SYSTEM_SYSRULES_forEach"(
    items: {
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_CANCELLED
    },
    expression: #"SYSTEM_SYSRULES_gridField_v2"(
      emptyGridMessage: "No tasks available",
      labelposition: "COLLAPSED",
      data: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!allTasks,
        field: "taskStatus.taskStatusId",
        value: fv!item
      ),
      columns: {
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Task",
          sortfield: "taskName",
          value: fv!row.taskName
        ),
        rule!AS_GSS_TMG_UI_taskGrid_colChecklistName(i18nData: ri!i18nData),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: if(
            fv!item = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
            "Completed",
            "Assignee"
          ),
          value: if(
            rule!AS_CO_UT_isNotBlank(fv!row.completedBy),
            if(
              and(
                ri!isEvaluatorMasked,
                tostring(
                  fv!row.taskBehaviorType.behaviorSubtypeCode
                ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: rule!AS_GSS_UT_returnNickName(
                      userNames: fv!row.completedBy,
                      nickNamesList: ri!evaluatorNickNames
                    )
                  )
                }
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text:rule!AS_CO_UT_displayUser(user: fv!row.completedBy))})

            ),
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              label: "Assignee",
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: rule!AS_CO_UT_displayGroupOrUser(groupOrUser: fv!row.groupAssignee)
                )
              }
            )
          ),
          width: "AUTO"
        ),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Phase",
          sortField: "taskCategory.categoryName",
          value: fv!row.taskCategory.categoryName
        ),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Available Since",
          sortfield: "availableDatetime",
          value: rule!AS_CO_UT_formatDateTime(datetime: fv!row.availableDatetime),
          helpTooltip: "Date and time this task was kicked off",
          showwhen: fv!item <> cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
          align: "END"
        ),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Closed",
          sortfield: "completedDatetime",
          value: rule!AS_CO_UT_formatDateTime(datetime: fv!row.completedDatetime),
          showwhen: fv!item = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
          align: "END"
        ),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Duration",
          value: rule!AS_CO_UT_formatInterval(
            startDatetime: rule!AS_CO_UT_replaceNull(
              nullableValue: fv!row.availableDatetime,
              replacementValue: fv!row.createdDatetime
            ),
            endDatetime: rule!AS_CO_UT_replaceNull(
              nullableValue: fv!row.completedDatetime,
              replacementValue: fv!row.modifiedDatetime
            )
          ),
          showWhen: contains(
            {
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
              cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED
            },
            fv!item
          )
        ),
        #"SYSTEM_SYSRULES_gridColumn"(
          label: "Regenerate",
          align: "CENTER",
          value: a!localVariables(
            /*Enabling reopening for "Complete evaluation" (when evaluation is not yet completed) in the system generated tasks and all other Adhoc tasks*/
            local!enableReopening: or(
              and(
                tointeger(
                  fv!row.evaluation.evaluationStatus.refDataId
                ) <> cons!AS_GSS_REF_ID_EVALUATION_STATUS_COMPLETE,
                tostring(
                  fv!row.taskBehaviorType.behaviorSubtypeCode
                ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
                /* "Complete evaluation" tasks can be re-generated only when the corresponding consensus is not started*/
                if(
                  rule!AS_CO_UT_isBlank(ri!startedConsensus),
                  true,
                  rule!AS_CO_UT_isBlank(
                    wherecontains(
                      wherecontains(
                        tointeger(fv!row.vendor.vendorId),
                        tointeger(ri!startedConsensus.vendorId)
                      ),
                      wherecontains(
                        tointeger(fv!row.criteria.criteriaId),
                        tointeger(ri!startedConsensus.factorId)
                      )
                    )
                  )
                )
              ),
              fv!row.taskBehaviorType.configurationLevelCode <> cons!AS_GSS_TMG_ENUM_TASK_CONFIGURATION_LEVEL_CODE_SYSTEM
            ),
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              align: "CENTER",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  altText: if(
                    local!enableReopening,
                    "Regenerate Item",
                    "System Generated Item"
                  ),
                  icon: "rotate-right",
                  color: if(
                    local!enableReopening,
                    "ACCENT",
                    "SECONDARY"
                  ),
                  caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: if(
                      local!enableReopening,
                      "cpt_RegenerateTask",
                      if(
                        tostring(
                          fv!row.taskBehaviorType.behaviorSubtypeCode
                        ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
                        "cpt_ConsensusEvaluationStarted",
                        "cpt_SystemGeneratedTask"
                      )
                    )
                  ),
                  linkStyle: "STANDALONE",
                  link: if(
                    rule!AS_CO_UT_isUserMemberOfGroups(
                      groups: cons!AS_GSS_GRP_ALL_BUSINESS_USERS,
                      user: loggedInUser()
                    ),
                    #"SYSTEM_SYSRULES_startProcessLink"(
                      processModel: cons!AS_GSS_TMG_PM_REOPEN_TASKS,
                      processParameters: {
                        tasksToReopen: rule!AS_GSS_createRunTimeTaskOnRegenerateItem(
                          /*Querying the specific task again as ri!allTasks is queried with selection*/
                          tasks: rule!AS_GSS_TMG_QE_getTasks(
                            returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                            taskIds: fv!row.taskId
                          )
                        )
                      },
                      showWhen: local!enableReopening
                    ),
                    ""
                  )
                )
              }
            )
          ),
          showWhen: local!showReopenTaskColumn
        )
      },
      spacing: "DENSE",
      borderstyle: "LIGHT",
      rowheader: 1
    )
  )
)

