data.sail_code:
a!localVariables(
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!evaluatorNickNames: rule!AS_GSS_QR_getMaskEvaluatorDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!evaluation.evaluationId,

  ),
  local!tasks: rule!AS_GSS_TMG_QE_getTasks(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationIds: ri!evaluation.evaluationId,
    sort: {
      #"SYSTEM_SYSRULES_sortInfo"(field: "availableDatetime"),
      #"SYSTEM_SYSRULES_sortInfo"(field: "taskId")
    },
    querySelection: #"SYSTEM_SYSRULES_querySelection"(
      columns: #"SYSTEM_SYSRULES_forEach"(
        items: {
          "taskId",
          "taskName",
          "userAssignee",
          "groupAssignee",
          "availableDatetime",
          "dueDate",
          "noOfDaysFromStart",
          "completedBy",
          "completedDatetime",
          "createdDatetime",
          "modifiedDatetime",
          "evaluation.evaluationStatus.refDataId",
          "evaluation.evaluationId",
          "taskStatus",
          "taskChecklist.listName",
          "taskPrecedents",
          "taskCategory.categoryName",
          "taskBehaviorType",
          "vendor.vendorId",
          "criteria.criteriaId"
        },
        expression: #"SYSTEM_SYSRULES_queryColumn"(field: fv!item)
      )
    )
  ),
  local!taskStatuses: rule!AS_GSS_TMG_QE_getTaskStatus(),
  local!selectedCategory,
  local!selectedTemplate,
  local!allTasks: if(
    and(
      rule!AS_CO_UT_isBlank(local!selectedCategory),
      rule!AS_CO_UT_isBlank(local!selectedTemplate)
    ),
    local!tasks,
    if(
      rule!AS_CO_UT_isBlank(local!selectedCategory),
      rule!AS_CO_UT_filterCdtByField(
        cdt: local!tasks,
        field: { "taskChecklist.templateId" },
        value: { local!selectedTemplate }
      ),
      if(
        rule!AS_CO_UT_isBlank(local!selectedTemplate),
        rule!AS_CO_UT_filterCdtByField(
          cdt: local!tasks,
          field: { "taskCategory.categoryName" },
          value: { local!selectedCategory }
        ),
        rule!AS_CO_UT_filterCdtByMultipleFields(
          cdt: local!tasks,
          fields: {
            "taskCategory.categoryName",
            "taskChecklist.templateId"
          },
          values: {
            local!selectedCategory,
            local!selectedTemplate
          }
        )
      )
    )
  ),
  local!outstandingTasks: rule!AS_CO_UT_filterCdtByField(
    cdt: local!allTasks,
    field: "taskStatus.taskStatusId",
    value: {
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ALL,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
    }
  ),
  local!categoryOptions: rule!AS_CO_UT_sortArray(
    array: rule!AS_CO_UT_distinct(
      value: rule!AS_CO_UT_rejectNullValuesFromArray(local!tasks.taskCategory.categoryName)
    )
  ),
  local!templateDetails: rule!AS_GSS_TMG_QE_getTemplate(
    templateTypeId: cons!AS_GSS_REF_ID_TEMPLATE_TYPE_CONTRACTING,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  local!selectedOutstandingTaskOption: cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ALL,
  local!selectedChecklistStatus: cons!AS_GSS_REF_ID_CHECKLIST_STATUS_OUTSTANDING,
  local!refDataForChecklistStatus: rule!AS_GSS_QE_getRefDataByTypes(
    refTypes: cons!AS_GSS_REF_TYPE_CHECKLIST_STATUS_TYPE
  ),
  local!closedTasksItemCount: #"SYSTEM_SYSRULES_forEach"(
    items: {
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED,
      cons!AS_GSS_TMG_REF_ID_TASK_STATUS_CANCELLED
    },
    expression: count(
      rule!AS_CO_UT_filterCdtByField(
        cdt: local!allTasks,
        field: "taskStatus.taskStatusId",
        value: fv!item
      )
    )
  ),
  local!outstandingTasksCount: count(local!outstandingTasks),
  local!totalItemCounts: rule!AS_CO_UT_rejectNullValuesFromArray(
    append(
      local!outstandingTasksCount,
      local!closedTasksItemCount
    )
  ),
  local!closedTasksIndexValue: local!selectedChecklistStatus - cons!AS_GSS_REF_ID_CHECKLIST_STATUS_OUTSTANDING,
  local!checklistNames: rule!AS_CO_UT_rejectValuesFromArray(
    array: union(local!tasks.taskChecklist.listName, "")
  ),
  local!startedConsensus: rule!AS_GSS_QE_getConsensusReport(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "status.refDataId",
        operator: "<>",
        value: cons!AS_GSS_REF_DATA_ID_CONSENSUS_REPORT_STATUS_NOT_STARTED
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: "evaluationId",
        operator: "=",
        value: ri!evaluation.evaluationId
      )
    }
  ),
  rule!AS_GSS_HCL_displayWrapperContents(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_sectionLayout_v1"(
                contents: {
                  /*task percentage completion*/
                  rule!AS_GSS_TMG_CP_taskPercentCompletion(
                    evaluation: ri!evaluation,
                    i18nData: local!i18nData
                  )
                },
                divider: "BELOW"
              ),
              /*Checklist status card navigation*/
              rule!AS_GSS_CPS_cardsForChecklistStatusNavigation(
                value: local!selectedChecklistStatus,
                saveInto: local!selectedChecklistStatus,
                checklistStatuses: local!refDataForChecklistStatus,
                itemCount: local!totalItemCounts,
                i18nData: local!i18nData
              ),
              /*checklist names*/
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: "Approach(es)",
                        style: "STRONG"
                      ),
                      " ",
                      #"SYSTEM_SYSRULES_richTextBulletedList"(items: local!checklistNames)
                    },
                    showWhen: rule!AS_CO_UT_isNotBlank(local!templateDetails.templateName[1])
                  )
                },
                showWhen: rule!AS_CO_UT_isNotBlank(local!checklistNames)
              )
            },
            width: "NARROW_PLUS"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /*empty space*/
              rule!AS_CO_DSP_emptySpacer(
                spacingHeight: cons!AS_CO_ENUM_EMPTY_SPACER_HEIGHT_SHORT
              ),
              /*Status header*/
              #"SYSTEM_SYSRULES_sectionLayout_v1"(
                label: displayvalue(
                  local!selectedChecklistStatus,
                  rule!AS_GSS_CONS_CHECKLIST_STATUS_OPTIONS(),
                  #"SYSTEM_SYSRULES_forEach"(
                    items: rule!AS_GSS_CONS_CHECKLIST_STATUS_OPTIONS(),
                    expression: rule!AS_CO_UT_displayRefDataLabel(
                      value: fv!item,
                      data: local!refDataForChecklistStatus,
                      valueField: "refDataId",
                      labelField: "refLabel"
                    )
                  ),
                  {}
                ) & " (" & displayvalue(
                  local!selectedChecklistStatus,
                  rule!AS_GSS_CONS_CHECKLIST_STATUS_OPTIONS(),
                  local!totalItemCounts,
                  {}
                ) & ")",
                labelColor: "STANDARD",
                labelHeadingTag: "H2",
                labelSize: "MEDIUM",
                contents: {
                  /*empty space*/
                  rule!AS_CO_DSP_emptySpacer(
                    spacingHeight: cons!AS_CO_ENUM_EMPTY_SPACER_HEIGHT_SHORT
                  ),
                  /*Checklist filters*/
                  rule!AS_GSS_SBS_checklistTabToolbar(
                    i18nData: local!i18nData,
                    categoryOptions: local!categoryOptions,
                    selectedCategory: local!selectedCategory,
                    selectedOutstandingTaskOption: local!selectedOutstandingTaskOption,
                    selectedStatus: local!selectedChecklistStatus,
                    taskStatuses: local!taskStatuses,
                    templates: local!templateDetails,
                    selectedTemplate: local!selectedTemplate
                  ),
                  /*all status grid details*/
                  if(
                    local!selectedChecklistStatus = cons!AS_GSS_REF_ID_CHECKLIST_STATUS_OUTSTANDING,
                    rule!AS_GSS_TMG_BOX_outstandingTasks(
                      i18nData: local!i18nData,
                      outstandingTasks: local!outstandingTasks,
                      tasksForOnboarding: local!tasks,
                      taskStatuses: local!taskStatuses,
                      pickedStatuses: if(
                        or(
                          local!selectedOutstandingTaskOption = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ALL,
                          rule!AS_CO_UT_isBlank(local!selectedOutstandingTaskOption)
                        ),
                        {
                          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
                          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
                        },
                        local!selectedOutstandingTaskOption
                      ),
                      isEvaluatorMasked: a!defaultValue(
                        ri!evaluation.isEvaluatorMasked,
                        false()
                      ),
                      evaluatorNickNames: local!evaluatorNickNames
                    ),
                    rule!AS_GSS_TMG_CPS_closedTasks(
                      allTasks: local!allTasks,
                      i18nData: local!i18nData,
                      startedConsensus: local!startedConsensus,
                      isEvaluatorMasked: a!defaultValue(
                        ri!evaluation.isEvaluatorMasked,
                        false()
                      ),
                      evaluatorNickNames: local!evaluatorNickNames
                    )[local!closedTasksIndexValue]
                  )
                }
              ),

            }
          )
        }
      )
    },
    backgroundColor: rule!AS_GSS_BrandingValueByKey(
      brandingKey: "DefaultBackground",
      useSuiteBranding: true
    )
  )
)

