data.sail_code:
a!localVariables(
  local!usernames,
  local!taskActionIds,
  local!date,
  local!dateDescending: true,
  local!pagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!AS_CO_ENUM_PAGE_SIZE_SMALL,
      sort: {
        #"SYSTEM_SYSRULES_sortInfo"(
          field: "timestamp",
          ascending: rule!AS_CO_UT_not(local!dateDescending)
        )
      }
    ),
    refreshOnVarChange: ri!selectedCategoryName
  ),
  local!isEvaluatorMasked: a!defaultValue(
    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked],
    false
  ),
  local!taskActionAudits: rule!AS_GSS_TMG_QE_getTaskActionAudit(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    evaluationId: ri!evaluationId,
    categoryName: ri!selectedCategoryName,
    taskActionIds: local!taskActionIds,
    usernames: local!usernames,
    date: local!date,
    pagingInfo: local!pagingInfo
  ),
  local!selectedTaskId: a!refreshVariable(
    value: null,
    refreshOnVarChange: {
      ri!selectedCategoryName,
      ri!evaluationId
    }
  ),
  local!selectedTaskAuditActions: rule!AS_GSS_TMG_QE_getTaskActionAudit(
    evaluationId: ri!evaluationId,
    taskId: local!selectedTaskId,
    categoryName: ri!selectedCategoryName,
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    executeWhen: rule!AS_CO_UT_isNotBlank(local!selectedTaskId),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: - 1,
      sort: local!pagingInfo.sort
    )
  ),
  local!taskActions: rule!AS_GSS_TMG_QE_getTaskActions(),
  local!showPreviousPagination: local!pagingInfo.startIndex <> 1,
  local!showNextPagination: local!pagingInfo.startIndex + local!pagingInfo.batchSize <= local!taskActionAudits.totalCount,
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_sideBySideLayout"(
              items: {
                /*User Field*/
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: rule!AS_CO_CP_pickerFieldRecord(
                    label: "Users",
                    labelPosition: "COLLAPSED",
                    recordType: recordType!AS_GSS_User_RECORD,
                    placeholder: "Search User(s)",
                    value: touniformstring(local!usernames),
                    saveInto: {
                      local!usernames,
                      a!save(local!pagingInfo.startIndex, 1)
                    },
                    /*Removing evaluators if masked*/
                    filters: #"SYSTEM_SYSRULES_queryFilter"(
                      field: recordType!AS_GSS_User_RECORD.username,
                      operator: "not in",
                      value: a!flatten(
                        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.criteria.CriteriaAssignment.assignee]
                      ),
                      applyWhen: local!isEvaluatorMasked
                    )
                  )
                ),
                /*Action Field*/
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: rule!AS_CO_CP_multipleDropdownField(
                    label: "Actions",
                    labelPosition: "COLLAPSED",
                    choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                      items: local!taskActions,
                      expression: rule!AS_CO_UT_displayRefDataLabelSingle(
                        singleData: fv!item,
                        labelField: "actionDisplayName",
                        i18nData: ri!i18nData
                      )
                    ),
                    choiceValues: local!taskActions.taskActionId,
                    placeholderLabel: "Select Action(s)",
                    value: local!taskActionIds,
                    saveInto: {
                      local!taskActionIds,
                      a!save(local!pagingInfo.startIndex, 1)
                    }
                  )
                ),
                /*Date Field*/
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: rule!AS_CO_INP_dateField(
                    label: "",
                    value: local!date,
                    saveInto: {
                      local!date,
                      a!save(local!pagingInfo.startIndex, 1)
                    },
                    labelPosition: "COLLAPSED"
                  ),
                  width: "MINIMIZE"
                ),
                #"SYSTEM_SYSRULES_sideBySideItem"(width: "MINIMIZE"),
                /*Clear Field*/
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                    labelposition: "COLLAPSED",
                    value: rule!AS_CO_UT_clearSelectionLink(
                      saveInto: {
                        rule!AS_CO_UT_saveNull(valueToNull: local!taskActionIds),
                        rule!AS_CO_UT_saveNull(valueToNull: local!date),
                        rule!AS_CO_UT_saveNull(valueToNull: local!usernames),
                        rule!AS_CO_UT_saveNull(valueToNull: ri!selectedCategoryName),
                        a!save(local!pagingInfo.startIndex, 1)
                      }
                    )
                  )
                ),
                /*Paging Field*/
