if(
  isnull(ri!colorSchemeValue),
  "",
  a!localVariables(
    local!hexCodes: rule!AS_FRM_parseColorSchemeBrandingConfig(
      colorSchemeValue: ri!colorSchemeValue,
      returnRawHexCodes: true
    ),
    local!isStandardColorScheme: contains(
      rule!AS_FRM_ENUM_STANDARD_CHART_COLOR_SCHEMES().key,
      ri!colorSchemeValue
    ),
    if(
      local!isStandardColorScheme,
      /* Standard color schemes behave (somewhat unintuitively) as follows --
      1) If there is only one color to be displayed, the second color in the scheme is used
      2) For pie charts, the colors are displayed backwards starting at the index of the number of colors to be displayed */
      if(
        ri!totalIndexes = 1,
        local!hexCodes[2],
        if(
          isnull(ri!colorIndex),
          local!hexCodes[1],
          a!localVariables(
            local!relevantHexCodes: index(
              local!hexCodes,
              enumerate(
                min(
                  if(
                    isnull(ri!totalIndexes),
                    {},
                    ri!totalIndexes
                  ),
                  count(local!hexCodes)
                )
              ) + 1
            ),
            local!relevantHexCodesOrdered: if(
              ri!isPieChart,
              reverse(local!relevantHexCodes),
              local!relevantHexCodes
            ),
            local!relevantHexCodesOrdered[mod(ri!colorIndex - 1, count(local!hexCodes)) + 1]
          )
        )
      ),
      /*custom color scheme*/
      local!hexCodes[mod(ri!colorIndex - 1, count(local!hexCodes)) + 1]
    )
  )
)

