              local!hexCodes,
              enumerate(
                min(
                  if(
                    isnull(ri!totalIndexes),
                    {},
                    ri!totalIndexes
                  ),
                  count(local!hexCodes)
                )
              ) + 1
            ),
            local!relevantHexCodesOrdered: if(
              ri!isPieChart,
              reverse(local!relevantHexCodes),
              local!relevantHexCodes
            ),
            local!relevantHexCodesOrdered[mod(ri!colorIndex - 1, count(local!hexCodes)) + 1]
          )
        )
      ),
      /*custom color scheme*/
      local!hexCodes[mod(ri!colorIndex - 1, count(local!hexCodes)) + 1]
    )
  )
)

