        extension: local!docExtension,
        document: rule!AS_CO_UT_indexPath(cdt: fv!row, field: ri!docIdFieldName),
        documentName: rule!AS_CO_UT_indexPath(cdt: fv!row, field: ri!docNameFieldName),
        sourceApplicationId: rule!AS_CO_UT_indexPath(cdt: fv!row, field: ri!docSourceFieldName)
