data.sail_code:
a!localVariables(
  local!externalVendorIds: rule!AS_CO_UT_rejectNullValuesFromArray(
    array: index(
      ri!evaluationVendors,
      "externalVendorId",
      null
    )
  ),
  local!getExternalVendorUpdates: rule!AS_GSS_QR_getExternalVendorUpdates(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    externalVendorIds: local!externalVendorIds,
    proposalActionId: cons!AS_GSS_REF_ID_PROPOSAL_ACTION_WITHDRAW,
    getActiveAndInactive: true(),
    evaluationId: ri!evaluationId,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      ascending: false(),
      field: recordType!AS_GSS_VendorUpdates_SYNCEDRECORD.updateId
    ),
    executeWhen: and(
      rule!AS_CO_UT_isNotBlank(local!externalVendorIds),
      rule!AS_CO_UT_isNotBlank(ri!evaluationId)
    )
  ),
  #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: {
      /*Evaluation audit header*/
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!header,
        style: "STRONG",
        color: "SECONDARY"
      ),
      #"SYSTEM_SYSRULES_richTextBulletedList"(
        items: {
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_rejectCdtByField(
              cdt: ri!auditEntry,
              field: "auditActionCode",
              value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
            ),
            /*Evaluation audit for individual audit entry*/
            expression: a!localVariables(
              local!individualAuditEntry: fv!item,
              local!subHeaderName: rule!AS_GSS_DSP_displaySubHeaderForAuditComment(
                individualAuditEntry: local!individualAuditEntry,
                evaluationCriterias: ri!evaluationCriterias,
                evaluationVendors: ri!evaluationVendors,
                evaluationPhases: ri!evaluationPhases,
                evaluatorTeams: ri!evaluatorTeams,
                subHeaderField: ri!subHeaderField,
                evaluatorNickNames: ri!evaluatorNickNames,
                isEvaluatorMasked: ri!isEvaluatorMasked
              ),
              local!individualAuditVendor: if(
                ri!subHeaderField = "legalName",
                rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationVendors,
                  field: "vendorId",
                  value: a!flatten(local!individualAuditEntry).vendorId
                ),
                null
              ),
              local!isProposalWithdrawnFromVm: rule!AS_CO_UT_isNotBlank(
                index(
                  wherecontains(
                    tointeger(local!individualAuditVendor.vendorId),
                    tointeger(
                      index(
                        local!getExternalVendorUpdates,
                        recordType!AS_GSS_VendorUpdates_SYNCEDRECORD.evalVendorId,
                        null
                      )
                    )
                  ),
                  1,
                  null
                )
              ),
              local!parentCriteriaName: if(
                and(
                  ri!subHeaderField = "criteriaName",
                  rule!AS_CO_UT_isNotBlank(
                    rule!AS_CO_UT_filterCdtByField(
                      cdt: ri!evaluationCriterias,
                      field: "criteriaId",
                      value: a!flatten(local!individualAuditEntry).criteriaId
                    ).parentCriteriaId
                  )
                ),
                rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!evaluationCriterias,
                  field: "criteriaId",
                  value: rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!evaluationCriterias,
                    field: "criteriaId",
                    value: a!flatten(local!individualAuditEntry).criteriaId
                  ).parentCriteriaId
                ).criteriaName,
                null
              ),
              local!complexFieldChanges: if(
                ri!subHeaderField = "teamName",
                rule!AS_CO_UT_indexPath(
                  cdt: a!flatten(local!individualAuditEntry),
                  field: "teamMembershipChanges"
                ),
                if(
                  ri!subHeaderField = "criteriaName",
                  rule!AS_CO_UT_indexPath(
                    cdt: a!flatten(local!individualAuditEntry),
                    field: "assignmentChanges"
                  ),
                  if(
                    ri!subHeaderField = "legalName",
                    rule!AS_CO_UT_indexPath(
                      cdt: local!individualAuditEntry,
                      field: "priceBreakupChanges"
                    ),
                    ""
                  )
                )
              ),
              local!foreignIdFields: displayvalue(
                ri!subHeaderField,
                { "teamName", "criteriaName", "legalName" },
                { "teamId", "criteriaId", "vendorId" },
                ""
              ),
              if(
                or(
                  rule!AS_CO_UT_isNotBlank(fv!item.simpleFieldChanges),
                  rule!AS_CO_UT_isNotBlank(
                    rule!AS_CO_UT_rejectCdtByField(
                      cdt: local!complexFieldChanges,
                      field: "auditActionCode",
                      value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
                    )
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    {
                      rule!AS_GSS_DSP_displayAuditActionCodeText(
                        i18nData: ri!i18nData,
                        actionCode: fv!item.auditActionCode,
                        displayNameForDeactivate: "Deleted",
                        /*colorCode: "STANDARD",*/
                        style: "PLAIN"
                      ),
                      " ",
                      if(
                        ri!subHeaderField = "criteriaName",
                        if(
                          rule!AS_CO_UT_isBlank(a!flatten(local!parentCriteriaName)),
                          "Factor ",
                          "Subfactor "
                        ),
                        ""
                      ),
                      local!subHeaderName,
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          " " & "from related opportunity"
                        },
                        showWhen: and(
                          rule!AS_CO_UT_isNotBlank(
                            local!individualAuditVendor.externalVendorId
                          ),
                          ri!subHeaderField = "legalName",
                          fv!item.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD
                        )
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          " " & cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT & " " & "Proposal withdrawn from related opportunity"
                        },
                        showWhen: and(
                          rule!AS_CO_UT_isNotBlank(
                            local!individualAuditVendor.externalVendorId
                          ),
                          local!isProposalWithdrawnFromVm,
                          ri!subHeaderField = "legalName",
                          fv!item.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DEACTIVATE
                        )
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          " " & "of parent factor" & " ",
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: local!parentCriteriaName,
                            style: "STRONG",
                            color: "SECONDARY"
                          )
                        },
                        showWhen: rule!AS_CO_UT_isNotBlank(local!parentCriteriaName)
                      ),

                    },
                    if(
                      contains(
                        {
                          cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
                          cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DEACTIVATE,
                          cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD
                        },
                        fv!item.auditActionCode
                      ),
                      {},
                      {
                        char(13),
                        #"SYSTEM_SYSRULES_richTextBulletedList"(
                          items: {
                            /*Displaying the audit comment for simple field changes*/
                            #"SYSTEM_SYSRULES_forEach"(
                              items: rule!AS_CO_UT_filterCdtByField(
                                cdt: local!individualAuditEntry.simpleFieldChanges,
                                field: "fieldName",
                                value: touniformstring(
                                  rule!AS_GSS_CONS_evaluationAuditFields()
                                )
                              ),
                              expression: rule!AS_GSS_UT_constructAuditCommentForEvaluationFields(
                                fieldName: fv!item.fieldName,
                                oldValue: fv!item.oldValue,
                                newValue: fv!item.newValue,
                                refData: ri!refData,
                                stateRefData: ri!stateRefData,
                                countryRefData: ri!countryRefData,
                                refRatingData: ri!refRatingData,
                                userDetails: ri!userDetails,
                                i18nData: ri!i18nData,
                                evaluatorTeams: ri!evaluatorTeams
                              )
                            ),
                            if(
                              rule!AS_CO_UT_isBlank(a!flatten(local!complexFieldChanges)),
                              {},
                              /*Displaying the audit comment for Complex fields of the evaluation CDTs*/
                              rule!AS_GSS_DSP_displayAuditCommentForEvaluationCdts(
                                i18nData: ri!i18nData,
                                auditEntry: rule!AS_CO_UT_castToMapList(local!complexFieldChanges),
                                refData: ri!refData,
                                evaluationPhases: ri!evaluationPhases,
                                /*Passing only the cuurent criteria details*/
                                evaluationCriterias: if(
                                  local!foreignIdFields = "criteriaId",
                                  rule!AS_CO_UT_filterCdtByField(
                                    cdt: ri!evaluationCriterias,
                                    field: "criteriaId",
                                    value: index(
                                      local!individualAuditEntry,
                                      "criteriaId",
                                      null
                                    )
                                  ),
                                  null
                                ),
                                /*Passing only the cuurent vendor details*/
                                evaluationVendors: if(
                                  local!foreignIdFields = "vendorId",
                                  rule!AS_CO_UT_filterCdtByField(
                                    cdt: ri!evaluationVendors,
                                    field: "vendorId",
                                    value: index(
                                      local!individualAuditEntry,
                                      "vendorId",
                                      null
                                    )
                                  ),
                                  null
                                ),
                                /*Passing only the cuurent team details*/
                                evaluatorTeams: if(
                                  local!foreignIdFields = "teamId",
                                  rule!AS_CO_UT_filterCdtByField(
                                    cdt: ri!evaluatorTeams,
                                    field: "teamId",
                                    value: index(
                                      local!individualAuditEntry,
                                      "teamId",
                                      null
                                    )
                                  ),
                                  null
                                ),
                                countryRefData: ri!countryRefData,
                                stateRefData: ri!stateRefData,
                                refRatingData: ri!refRatingData,
                                header: displayvalue(
                                  ri!subHeaderField,
                                  { "teamName", "criteriaName", "legalName" },
                                  {
                                    "Team Membership",
                                    "Factor Assignments",
                                    "Price Breakup"
                                  },
                                  ""
                                ),
                                subHeaderField: displayvalue(
                                  ri!subHeaderField,
                                  { "teamName", "criteriaName", "legalName" },
                                  { "member", "assignee", "itemDescription" },
                                  ""
                                ),
                                userDetails: ri!userDetails,
                                evaluatorNickNames: ri!evaluatorNickNames,
                                isEvaluatorMasked: ri!isEvaluatorMasked
                              )
                            )
                          }
                        )
                      }
                    )
                  }
                ),
                {}
              )
            )
          )
        }
      )
    },
    showWhen: rule!AS_CO_UT_isNotBlank(
      rule!AS_CO_UT_rejectCdtByField(
        cdt: ri!auditEntry,
        field: "auditActionCode",
        value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
      )
    )
  )
)

