    rule!AS_CO_UT_isNotBlank(ri!castToCdtType),
    not(contains(
      {cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY, cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT, cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET},
      ri!returnType
    )),
  ),
  fn!error("'castToCdtType' is only supported for SINGLE_OBJECT, OBJECT_ARRAY, and DATA_SUBSET return types"),

  a!localVariables(

    /*Determine the castResultToType*/
    local!castResultToType: rule!AS_CO_UT_displayValue(
      value: ri!returnType,
      inArray: rule!AS_CO_CONS_QE_RETURN_TYPES(),
      replacement: {
        /*DATA_SUBSET*/   'type!{http://www.appian.com/ae/types/2009}Map',
        /*SINGLE_OBJECT*/ a!defaultValue(ri!castToCdtType, ri!recordType),
        /*OBJECT_ARRAY*/  rule!AS_CO_UT_returnArrayTypeNumber(type: a!defaultValue(ri!castToCdtType, ri!recordType)),
        /*AGGREGATION*/   'type!{http://www.appian.com/ae/types/2009}Map?list',
        /*TOTAL_COUNT*/   'type!{http://www.appian.com/ae/types/2009}Integer',
      }
    ),

    /*Determine the batchSize*/
    local!batchSize: rule!AS_CO_UT_displayValue(
      value: ri!returnType,
      inArray: rule!AS_CO_CONS_QE_RETURN_TYPES(),
      replacement: {
        /*DATA_SUBSET*/   ri!pagingInfo.batchSize,
        /*SINGLE_OBJECT*/ 1,
        /*OBJECT_ARRAY*/  a!defaultValue(ri!pagingInfo.batchSize, cons!AS_CO_INT_QUERY_MAX_BATCH_SIZE),
        /*AGGREGATION*/   cons!AS_CO_INT_QUERY_MAX_BATCH_SIZE,
        /*TOTAL_COUNT*/   1,
      }
    ),

    /*Determine the pagingInfo*/
    local!pagingInfo: if(
      ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      /*Use specific pagingInfo for DATA_SUBSET*/
      ri!pagingInfo,
      /*Otherwise calculate pagingInfo for all other returnTypes*/
      #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: local!batchSize,
        sort: ri!sort
      )
    ),

