    local!fetchTotalCount: rule!AS_CO_UT_displayValue(
      value: ri!returnType,
      inArray: rule!AS_CO_CONS_QE_RETURN_TYPES(),
      replacement: {
        /*DATA_SUBSET*/   true,
        /*SINGLE_OBJECT*/ false,
        /*OBJECT_ARRAY*/  false,
        /*AGGREGATION*/   false,
        /*TOTAL_COUNT*/   true,
      }
    ),

