rule!AS_CO_UT_rejectNullValuesFromArray(
  {
    /*Returns text only if all parent factor weights <> NULL and total factor weight <> 100*/
    if(
      and(
        rule!AS_CO_UT_isNotBlank(ri!evaluationFactors),
        rule!AS_GSS_UT_checkIfAnyWeightIsNullAndMismatchedTotalWeight(
          totalWeight: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorsTotalWeight],
          allFactorsWeights: ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
        )
      ),
      "Factor weights should add up to 100",
      null
    ),
    /*Returns parent factors with all subfactor weights <> NULL and its sum <> total factor weight*/
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!evaluationFactors,
      expression: a!localVariables(
        local!associatedSubfactors: rule!AS_CO_UT_filterRecordByFieldValues(
          records: ri!evaluationSubFactors,
          field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
          values: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
        ),
        local!sumOfSubfactorWeights: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.subFactorsTotalWeight],
        if(
          and(
            not(
              rule!AS_GSS_UT_checkIfNullOrZero(
                input: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
              )
            ),
            rule!AS_GSS_UT_isNotBlank(value: local!associatedSubfactors),
            rule!AS_GSS_UT_checkIfAnyWeightIsNullAndMismatchedTotalWeight(
              totalWeight: local!sumOfSubfactorWeights,
              allFactorsWeights: local!associatedSubfactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight],
              parentFactorWeight: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorWeight]
            )
          ),
          concat(
            char(34),
            index(
              fv!item,
              recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
              null
            ),
            char(34),
            " ",
            "factor weight should be equal to the sum of all related sub factors weights"
          ),
          null
        )
      )
    ),
    /*Returns text only if any factor weights is null*/
    if(
      and(
        ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.subFactorCount] = ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.weightedSubfactorCount],
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorCount] = ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.weightedFactorCount]
      ),
      {},
      "Enter weight greater than 0 for all factors/sub factors",

    )
  }
)

