or(
  /*Check if all tasks are blank*/
  rule!AS_CO_UT_isBlank(ri!selectedTasks),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!selectedTasks,
    expression: if(
      /*Returns false (enabled) when the task behaviour type is "Complete Evaluation"*/
      tostring(
        fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
      ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
      /*Changed it to true because we are not using claim tasks for criteria tasks only using it for process tasks*/
      true,
      if(
        and(
          tostring(
            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
          ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_LPTA_EVALUATION,
          contains(
            touser(
              {
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingOfficer],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.contractingSpecialist],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationChief],
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
              }
            ),
            ri!user
          )
        ),
        false(),
        if(
          /*First check that the task status is assigned*/
          rule!AS_CO_UT_not(
            contains(
              {
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
              },
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
            )
          ),
          true,
          if(
            /*Then check if there is a default assignee on the task*/
            rule!AS_CO_UT_isNotBlank(
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
            ),
            /*Return false (not disabled) if EITHER the user is in the default assignee group OR the user is the individual assignee*/
            and(
              rule!AS_CO_UT_not(
                rule!AS_CO_UT_isUserMemberOfGroups(
                  groups: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee],
                  user: loggedInUser()
                )
              ),
              rule!AS_CO_UT_not(
                fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
              )
            ),
            /*Return false (not disabled) if the user is the individual assignee*/
            rule!AS_CO_UT_not(
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
            )
          )
        )
      )
    )
  )
)

