a!localVariables(
  local!assigned: ri!task.taskStatus.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
  local!queuedWithPrecedents: and(
    ri!task.taskStatus.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED,
    rule!AS_CO_UT_isNotBlank(ri!task.taskPrecedents)
  ),
  #"SYSTEM_SYSRULES_gridRowLayout"(
    contents: {
      /*Task Name*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: ri!task.taskStatus.icon,
            caption: rule!AS_CO_UT_displayRefDataLabelSingle(
              singleData: ri!task.taskStatus,
              labelField: "statusDisplayName",
              i18nData: ri!i18nData
            ),
            altText: rule!AS_CO_UT_displayRefDataLabelSingle(
              singleData: ri!task.taskStatus,
              labelField: "statusDisplayName",
              i18nData: ri!i18nData
            ),

          ),
          " ",
          ri!task.taskName,
          /*allow expansion when has precedents*/
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: " ",
            showWhen: local!queuedWithPrecedents
          ),
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: if(
              ri!expand,
              "chevron-down",
              "chevron-right"
            ),
            caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: if(ri!expand, "cpt_Hide", "cpt_Show")
            ) & " " & "Precedents",
            altText: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: if(ri!expand, "cpt_Hide", "cpt_Show")
            ) & " " & "Precedents",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: rule!AS_CO_UT_not(ri!expand),
              saveInto: ri!expand
            ),
            linkStyle: "STANDALONE",
            showWhen: local!queuedWithPrecedents
          )
        },
        showWhen: ri!isParent
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          char(9),
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: ri!precedingTask.taskStatus.icon,
            caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: ri!precedingTask.taskStatus.statusDisplayName
            ),
            altText: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: ri!precedingTask.taskStatus.statusDisplayName
            ),
            showWhen: ri!precedingTask.taskStatus.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_QUEUED
          ),
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "check",
            caption: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: if(
                ri!precedingTask.taskStatus.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
                "cpt_Done",
                "cpt_NotNeededCancelled"
              )
            ),
            altText: rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: ri!i18nData,
              bundleKey: if(
                ri!precedingTask.taskStatus.taskStatusId = cons!AS_GSS_TMG_REF_ID_TASK_STATUS_COMPLETE,
                "cpt_Done",
                "cpt_NotNeededCancelled"
              )
            ),
            showWhen: contains(
              {
                cons!AS_GSS_TMG_REF_ID_TASK_ACTION_COMPLETED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_NOT_NEEDED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_CANCELLED
              },
              ri!task.taskStatus.taskStatusId
            ),
            color: ri!precedingTask.taskStatus.color
          ),
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: ri!precedingTask.taskStatus.icon,
            caption: rule!AS_CO_UT_displayRefDataLabel(
              value: cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
              data: ri!taskStatuses,
              valueField: "taskStatusId",
              labelField: "statusDisplayName",
              i18nData: ri!i18nData
            ),
            showWhen: contains(
              {
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
                cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
              },
              ri!precedingTask.taskStatus.taskStatusId
            )
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(text: " " & ri!precedingTask.taskName)
        },
        showWhen: ri!isParent = false
      ),
      /*Checklist Name*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_CO_UT_replaceNull(
            nullableValue: if(
              ri!isParent = true,
              ri!task.taskChecklist.listName,
              ri!precedingTask.taskChecklist.listName
            ),
            replacementValue: "-"
          )
        )
      ),
      /*Assignee*/
      if(
        rule!AS_CO_UT_isNotBlank(ri!task.userAssignee),
        if(
          and(
            ri!isEvaluatorMasked,
            tostring(
              ri!task.taskBehaviorType.behaviorSubtypeCode
            ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_GSS_UT_returnNickName(
                  userNames: ri!task.userAssignee,
                  nickNamesList: ri!evaluatorNickNames
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_CO_UT_displayUser(user: ri!task.userAssignee)
              )
            }
          )
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          label: "Assignee",
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AS_CO_UT_displayGroupOrUser(groupOrUser: ri!task.groupAssignee)
            )
          }
        )
      ),
      /*Category*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: ri!task.taskCategory.categoryName),
        align: "LEFT"
      ),
      /*Open*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_CO_UT_formatInterval(
            startDatetime: ri!task.availableDatetime,
            endDatetime: now()
          ),
          showWhen: local!assigned
        )
      ),
      /*Available*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_CO_UT_formatDateTime(datetime: ri!task.availableDatetime),
          showWhen: local!assigned
        ),
        align: "RIGHT"
      ),
      /*Initiate*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        align: "CENTER",
        accessibilityText: if(
          rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
          "Click here to send out queue task",
          "Click here to check task is available"
        ),
        value: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "paper-plane-o",
          caption: if(
            rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
            "Send out queued task",
            "Task is available"
          ),
          altText: if(
            rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
            "Send out queued task",
            "Task is available"
          ),
          link: #"SYSTEM_SYSRULES_startProcessLink"(
            processModel: cons!AS_GSS_TMG_PM_INITIATE_TASKS,
            processParameters: {
              tasksToInitiate: /*To update due date for Queued review document tasks*/
              rule!AS_CO_UT_updateCdtFields(
                /*Querying the specific task again as ri!task is queried with selection*/
                cdt: rule!AS_GSS_TMG_QE_getTasks(
                  returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                  taskIds: ri!task.taskId
                ),
                fields: "dueDate",
                newValues: rule!AS_GSS_TMG_UT_calculateDuedateForTasksWithPrecedents(task: ri!task)
              )
            },
            showWhen: rule!AS_CO_UT_isBlank(ri!task.availableDatetime)
          ),
          linkStyle: "STANDALONE"
        ),
        showWhen: ri!showInitiateTaskColumn
      )
    }
  )
)

