            tostring(
              ri!task.taskBehaviorType.behaviorSubtypeCode
            ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_GSS_UT_returnNickName(
                  userNames: ri!task.userAssignee,
                  nickNamesList: ri!evaluatorNickNames
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_CO_UT_displayUser(user: ri!task.userAssignee)
              )
            }
          )
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          label: "Assignee",
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AS_CO_UT_displayGroupOrUser(groupOrUser: ri!task.groupAssignee)
            )
          }
        )
      ),
      /*Category*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(text: ri!task.taskCategory.categoryName),
        align: "LEFT"
      ),
      /*Open*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_CO_UT_formatInterval(
            startDatetime: ri!task.availableDatetime,
            endDatetime: now()
          ),
          showWhen: local!assigned
        )
      ),
      /*Available*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_CO_UT_formatDateTime(datetime: ri!task.availableDatetime),
          showWhen: local!assigned
        ),
        align: "RIGHT"
      ),
      /*Initiate*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        align: "CENTER",
        accessibilityText: if(
          rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
          "Click here to send out queue task",
          "Click here to check task is available"
        ),
        value: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "paper-plane-o",
          caption: if(
            rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
            "Send out queued task",
            "Task is available"
          ),
          altText: if(
            rule!AS_CO_UT_isBlank(ri!task.availableDatetime),
            "Send out queued task",
            "Task is available"
          ),
          link: #"SYSTEM_SYSRULES_startProcessLink"(
            processModel: cons!AS_GSS_TMG_PM_INITIATE_TASKS,
            processParameters: {
              tasksToInitiate: /*To update due date for Queued review document tasks*/
              rule!AS_CO_UT_updateCdtFields(
                /*Querying the specific task again as ri!task is queried with selection*/
                cdt: rule!AS_GSS_TMG_QE_getTasks(
                  returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                  taskIds: ri!task.taskId
                ),
                fields: "dueDate",
                newValues: rule!AS_GSS_TMG_UT_calculateDuedateForTasksWithPrecedents(task: ri!task)
              )
            },
            showWhen: rule!AS_CO_UT_isBlank(ri!task.availableDatetime)
          ),
          linkStyle: "STANDALONE"
        ),
        showWhen: ri!showInitiateTaskColumn
      )
    }
  )
)

