a!localVariables(
  local!returnFlat: a!defaultValue(ri!returnFlat, true()),
  local!allFactors: rule!AS_GSS_QR_getCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    fields: ri!fields,
    evaluationId: ri!evaluationId,
    triggerRefresh: ri!triggerRefresh,
    includeRelatedRecords: false(),
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaStatusId,
      operator: "<>",
      value: cons!AS_GSS_REF_ID_EVALUATION_STATUS_TEMP
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!AS_GSS_INT_QUERY_RECORD_MAX_BATCH_SIZE
    )
  ).data,
  local!allAssignments: rule!AS_GSS_QR_getCriteriaAssignment(
    executeWhen: rule!AS_CO_UT_isNotBlank(
      local!allFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
    ),
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    criteriaId: tointeger(
      local!allFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
    ),
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.isActive,
        operator: "=",
        value: true()
      )
    },
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!AS_GSS_INT_QUERY_RECORD_MAX_BATCH_SIZE
    )
  ).data,
  local!updatedFactors: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(
      value: local!allAssignments[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId]
    ),
    local!allFactors,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!allFactors,
        expression: rule!AS_GSS_UT_updateRecordsByModelRecord(
          records: fv!item,
          modelRecord: recordType!AS_GSS_Criteria_SYNCEDRECORD(
            recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment: rule!AS_CO_UT_indexWhere(
              value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId],
              initialArray: local!allAssignments[recordType!AS_GSS_CriteriaAssignment_SYNCEDRECORD.criteriaId],
              finalArray: local!allAssignments,
              isRemove: false()
            )
          )
        )
      )
    )
  ),
  if(
    local!returnFlat,
    local!updatedFactors,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!updatedFactors,
        expression: if(
          rule!AS_CO_UT_checkIsNullOrEmpty(
            value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId]
          ),
          rule!AS_GSS_UT_updateRecordsByModelRecord(
            records: fv!item,
            modelRecord: recordType!AS_GSS_Criteria_SYNCEDRECORD(
              recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria: rule!AS_CO_UT_indexWhere(
                value: fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId],
                initialArray: local!updatedFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId],
                finalArray: local!updatedFactors,
                isRemove: false()
              )
            )
          ),
          {}
        )
      )
    )
  )
)

