data.sail_code:
if(
  tointeger(
    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatus.refDataId]
  ) = cons!AS_GSS_REF_ID_EVALUATION_STATUS_SETTING_UP,
  a!localVariables(
    /*No factor*/
    local!noFactor: rule!AS_GSS_UT_checkIfNullOrZero(
      ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorCount]
    ),
    /*Factor added,no teams or Assignment*/
    local!facWithNoTeamAssignment: and(
      tointeger(
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorCount]
      ) <> 0,
      rule!AS_GSS_UT_checkIfNullOrZero(
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.teamCount]
      ),
      rule!AS_GSS_UT_checkIfNullOrZero(
        sum(
          index(
            ri!evaluationFactors,
            recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount,
            0
          )
        )
      ),
      rule!AS_GSS_UT_checkIfNullOrZero(
        sum(
          index(
            ri!evaluationSubfactors,
            recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount,
            0
          )
        )
      )
    ),
    local!missingItems: {
      /*Details*/
      a!map(
        sectionName: "Details",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            if(
              and(
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                ),
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                ),
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
                ),

              ),
              "Complete all missing fields in the Details section",
              {
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                  ),
                  "Enter the Evaluation Start Date",
                  null
                ),
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                  ),
                  "Enter the Evaluation Due Date",
                  null
                ),
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
                  ),
                  "Select an Evaluation Method",
                  null
                )
              }
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateDetails",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Description*/
      a!map(
        sectionName: "Description",
        message: if(
          rule!AS_CO_UT_isBlank(
            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDescription]
          ),
          "Enter an Evaluation description",
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateDescription",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Phases*/
      a!map(
        sectionName: "Phases",
        message: a!defaultValue(
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseCount]
            ),
            "Add at least one Evaluation phase",
            if(
              or(
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseMaxDueDate]
                ),
                todate(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseMaxDueDate]
                ) > todate(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                )
              ),
              "Enter valid start dates for all phases",
              null
            )
          ),
          null
        ),
        parentActionConfig: {
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseCount]
            ),
            a!map(
              actionKey: "addPhases",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            ),
            a!map(
              actionKey: "updatePhases",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            )
          )
        },
        childActionConfig: {}
      ),
      /*Personnel*/
      a!map(
        sectionName: "Personnel",
        message: if(
          or(
            and(
              cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationChief]
              )
            ),
            and(
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingOfficer]
              ),
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingSpecialist]
              )
            )
          ),
          if(
            cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
            "Assign the Evaluation Chief and a Contracting Officer or Contract Specialist",
            "Assign a Contracting Officer or Contract Specialist"
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updatePersonnel",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Vendors*/
      a!map(
        sectionName: "Vendors",
        message: if(
          local!noFactor,
          {},
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendorCount]
            ),
            "Add at least one vendor to the Evaluation",
            {}
          )
        ),
        parentActionConfig: {
          a!map(
            actionKey: "addVendor",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      a!localVariables(
        local!incompleteVendors: #"SYSTEM_SYSRULES_forEach"(
          items: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
          expression: a!map(
            vendorId: if(
              or(
                fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0,
                rule!AS_GSS_UT_checkIfZero(
                  input: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.minimumFactorMappedCount]
                ),
                rule!AS_CO_UT_isBlank(
                  value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                )
              ),
              fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId],
              null
            ),
            message: {
              if(
                or(
                  and(
                    fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0,
                    rule!AS_CO_UT_isBlank(
                      value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                    )
                  ),
                  fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0
                ),
                concat(
                  "Attach at least one document for vendor",
                  " ",
                  char(34),
                  fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName],
                  char(34),
                  " ",
                  "and link it to a factor or subfactor"
                ),
                if(
                  rule!AS_GSS_UT_checkIfZero(
                    input: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.minimumFactorMappedCount]
                  ),
                  rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: "vld_VendorDocsWithNoFactors",
                    arguments: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName]
                  ),
                  if(
                    rule!AS_CO_UT_isBlank(
                      value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                    ),
                    rule!AS_GAM_CO_I18N_UT_displayLabel(
                      bundle: ri!i18nData,
                      bundleKey: "vld_VendorWithNoTotalPrice",
                      arguments: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName]
                    ),
                    null
                  )
                )
              )
            }
          )
        ),
        local!anyVendorWithUpdateRequired: or(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            array: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor.isUpdateRequired]
          )
        ),
        a!map(
          sectionName: "Vendors",
          message: a!defaultValue(
            value: append(
              rule!AS_CO_UT_rejectNullValuesFromArray(array: local!incompleteVendors.message),
              if(
                local!anyVendorWithUpdateRequired,
                "Review vendors with updated information",
                {}
              )
            ),
            default: null
          ),
          parentActionConfig: {},
          childActionConfig: {
            #"SYSTEM_SYSRULES_forEach"(
              items: local!incompleteVendors.vendorId,
              expression: if(
                rule!AS_CO_UT_checkIsNullOrEmpty(value: fv!item),
                {},
                a!map(
                  actionKey: "updateVendor",
                  identifier: fv!item
                )
              )
            )
          }
        )
      ),
      /*Evaluation Factors*/
      a!map(
        sectionName: "Evaluation Factors",
        message: if(
          local!noFactor,
          "Add at least one factor and assign it to an Evaluation team",
          if(
            local!facWithNoTeamAssignment,
            "Add at least one Evaluation team and assign it to factors",
            null
          )
        ),
        parentActionConfig: {
          if(
            local!noFactor,
            a!map(
              actionKey: "updateFactors",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            ),
            if(
              local!facWithNoTeamAssignment,
              a!map(
                actionKey: "addTeam",
                identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
              ),
              {}
            )
          )
        },
        childActionConfig: {}
      ),
      /*Factors*/
      a!map(
        sectionName: "Factors",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            {
              if(
                rule!AS_CO_UT_isNotBlank(
                  ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                ),
                #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterRecordByFieldValues(
                    records: ri!evaluationFactors,
                    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
                    values: true
                  ),
                  expression: {
                    if(
                      /*Any factor fields null*/
                      fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorFieldsBlank],
                      concat(
                        "Complete all missing details for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "factor"
                      ),
                      null
                    ),
                    /*Factor Date Validation*/
                    if(
                      or(
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) > todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                          ),

                        ),
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) < todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          )
                        )
                      ),
                      concat(
                        "Enter a valid due date for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "factor"
                      ),
                      null
                    )
                  }
                ),
                null
              ),
              if(
                rule!AS_CO_UT_isNotBlank(
                  ri!evaluationSubfactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                ),
                #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterRecordByFieldValues(
                    records: ri!evaluationSubfactors,
                    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
                    values: true
                  ),
                  expression: {
                    /*Any Subfactor fields null*/
                    if(
                      fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorFieldsBlank],
                      concat(
                        "Complete all missing details for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "subfactor"
                      ),
                      null
                    ),
                    /*Subfactor Date Validation*/
                    if(
                      or(
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) < todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),

                        ),
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            todate(
                              index(
                                index(
                                  ri!evaluationFactors,
                                  wherecontains(
                                    fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId],
                                    ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                                  ),
                                  null
                                ),
                                recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate,
                                null
                              )
                            )
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) > todate(
                            index(
                              index(
                                ri!evaluationFactors,
                                wherecontains(
                                  fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId],
                                  ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                                ),
                                null
                              ),
                              recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate,
                              null
                            )
                          )
                        )
                      ),
                      concat(
                        "Enter a valid due date for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "subfactor"
                      ),
                      null
                    )
                  }
                ),
                null
              )
            }
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateFactors",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Weights*/
      a!map(
        sectionName: "Weights",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            if(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isWeightedFactorsRequired],
              rule!AS_GSS_VD_validateFactorWeightsWithRecord(
                evaluationFactors: ri!evaluationFactors,
                evaluationSubFactors: ri!evaluationSubfactors,
                i18nData: ri!i18nData,
                evaluation: ri!evaluation
              ),
              null
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateWeights",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Teams*/
      a!map(
        sectionName: "Teams",
        message: /*This can be handled by */
        /*1. adding custom field in teams record to hold the aggreagetd count of members in that team.*/
        a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterRecordByFieldValues(
                records: ri!evaluatorTeams,
                field: recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.isActive,
                values: true
              ),
              expression: if(
                fv!item[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.membersCount] = 0,
                concat(
                  "Add team members to the",
                  " ",
                  char(34),
                  index(
                    fv!item,
                    recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamName,
                    null
                  ),
                  char(34),
                  " ",
                  "team"
                ),
                null
              )
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateTeams",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Assignment*/
      a!map(
        sectionName: "Assignment",
        message: if(
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: {
                ri!evaluationFactors,
                ri!evaluationSubfactors
              },
              expression: rule!AS_GSS_UT_checkIfNullOrZero(
                fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount]
              )
            )
          ),
          "Assign due dates and Evaluators to all Factors",
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateAssignment",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      )
    },
    local!filteredMissingItems: rule!AS_CO_UT_rejectNullValuesFromArray(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!missingItems,
        expression: if(
          a!isNullOrEmpty(fv!item.message),
          null,
          fv!item
        )
      )
    ),
    local!filteredMissingItems
  ),
  {}
)

data.definition:
if(
  tointeger(
    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStatus.refDataId]
  ) = cons!AS_GSS_REF_ID_EVALUATION_STATUS_SETTING_UP,
  a!localVariables(
    /*No factor*/
    local!noFactor: rule!AS_GSS_UT_checkIfNullOrZero(
      ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorCount]
    ),
    /*Factor added,no teams or Assignment*/
    local!facWithNoTeamAssignment: and(
      tointeger(
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.factorCount]
      ) <> 0,
      rule!AS_GSS_UT_checkIfNullOrZero(
        ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.teamCount]
      ),
      rule!AS_GSS_UT_checkIfNullOrZero(
        sum(
          index(
            ri!evaluationFactors,
            recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount,
            0
          )
        )
      ),
      rule!AS_GSS_UT_checkIfNullOrZero(
        sum(
          index(
            ri!evaluationSubfactors,
            recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount,
            0
          )
        )
      )
    ),
    local!missingItems: {
      /*Details*/
      a!map(
        sectionName: "Details",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            if(
              and(
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                ),
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                ),
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
                ),

              ),
              "Complete all missing fields in the Details section",
              {
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                  ),
                  "Enter the Evaluation Start Date",
                  null
                ),
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                  ),
                  "Enter the Evaluation Due Date",
                  null
                ),
                if(
                  rule!AS_CO_UT_isBlank(
                    ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationMethodId]
                  ),
                  "Select an Evaluation Method",
                  null
                )
              }
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateDetails",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Description*/
      a!map(
        sectionName: "Description",
        message: if(
          rule!AS_CO_UT_isBlank(
            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDescription]
          ),
          "Enter an Evaluation description",
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateDescription",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Phases*/
      a!map(
        sectionName: "Phases",
        message: a!defaultValue(
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseCount]
            ),
            "Add at least one Evaluation phase",
            if(
              or(
                rule!AS_CO_UT_isBlank(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseMaxDueDate]
                ),
                todate(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseMaxDueDate]
                ) > todate(
                  ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                )
              ),
              "Enter valid start dates for all phases",
              null
            )
          ),
          null
        ),
        parentActionConfig: {
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationPhaseCount]
            ),
            a!map(
              actionKey: "addPhases",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            ),
            a!map(
              actionKey: "updatePhases",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            )
          )
        },
        childActionConfig: {}
      ),
      /*Personnel*/
      a!map(
        sectionName: "Personnel",
        message: if(
          or(
            and(
              cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationChief]
              )
            ),
            and(
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingOfficer]
              ),
              rule!AS_CO_UT_isBlank(
                ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.contractingSpecialist]
              )
            )
          ),
          if(
            cons!AS_GSS_TOGGLE_EVALUATION_CHIEF_VISIBILITY,
            "Assign the Evaluation Chief and a Contracting Officer or Contract Specialist",
            "Assign a Contracting Officer or Contract Specialist"
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updatePersonnel",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Vendors*/
      a!map(
        sectionName: "Vendors",
        message: if(
          local!noFactor,
          {},
          if(
            rule!AS_GSS_UT_checkIfNullOrZero(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendorCount]
            ),
            "Add at least one vendor to the Evaluation",
            {}
          )
        ),
        parentActionConfig: {
          a!map(
            actionKey: "addVendor",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      a!localVariables(
        local!incompleteVendors: #"SYSTEM_SYSRULES_forEach"(
          items: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor],
          expression: a!map(
            vendorId: if(
              or(
                fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0,
                rule!AS_GSS_UT_checkIfZero(
                  input: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.minimumFactorMappedCount]
                ),
                rule!AS_CO_UT_isBlank(
                  value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                )
              ),
              fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId],
              null
            ),
            message: {
              if(
                or(
                  and(
                    fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0,
                    rule!AS_CO_UT_isBlank(
                      value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                    )
                  ),
                  fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorDocCount] = 0
                ),
                concat(
                  "Attach at least one document for vendor",
                  " ",
                  char(34),
                  fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName],
                  char(34),
                  " ",
                  "and link it to a factor or subfactor"
                ),
                if(
                  rule!AS_GSS_UT_checkIfZero(
                    input: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.minimumFactorMappedCount]
                  ),
                  rule!AS_GAM_CO_I18N_UT_displayLabel(
                    bundle: ri!i18nData,
                    bundleKey: "vld_VendorDocsWithNoFactors",
                    arguments: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName]
                  ),
                  if(
                    rule!AS_CO_UT_isBlank(
                      value: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.totalPrice]
                    ),
                    rule!AS_GAM_CO_I18N_UT_displayLabel(
                      bundle: ri!i18nData,
                      bundleKey: "vld_VendorWithNoTotalPrice",
                      arguments: fv!item[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName]
                    ),
                    null
                  )
                )
              )
            }
          )
        ),
        local!anyVendorWithUpdateRequired: or(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            array: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor.isUpdateRequired]
          )
        ),
        a!map(
          sectionName: "Vendors",
          message: a!defaultValue(
            value: append(
              rule!AS_CO_UT_rejectNullValuesFromArray(array: local!incompleteVendors.message),
              if(
                local!anyVendorWithUpdateRequired,
                "Review vendors with updated information",
                {}
              )
            ),
            default: null
          ),
          parentActionConfig: {},
          childActionConfig: {
            #"SYSTEM_SYSRULES_forEach"(
              items: local!incompleteVendors.vendorId,
              expression: if(
                rule!AS_CO_UT_checkIsNullOrEmpty(value: fv!item),
                {},
                a!map(
                  actionKey: "updateVendor",
                  identifier: fv!item
                )
              )
            )
          }
        )
      ),
      /*Evaluation Factors*/
      a!map(
        sectionName: "Evaluation Factors",
        message: if(
          local!noFactor,
          "Add at least one factor and assign it to an Evaluation team",
          if(
            local!facWithNoTeamAssignment,
            "Add at least one Evaluation team and assign it to factors",
            null
          )
        ),
        parentActionConfig: {
          if(
            local!noFactor,
            a!map(
              actionKey: "updateFactors",
              identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
            ),
            if(
              local!facWithNoTeamAssignment,
              a!map(
                actionKey: "addTeam",
                identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
              ),
              {}
            )
          )
        },
        childActionConfig: {}
      ),
      /*Factors*/
      a!map(
        sectionName: "Factors",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            {
              if(
                rule!AS_CO_UT_isNotBlank(
                  ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                ),
                #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterRecordByFieldValues(
                    records: ri!evaluationFactors,
                    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
                    values: true
                  ),
                  expression: {
                    if(
                      /*Any factor fields null*/
                      fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorFieldsBlank],
                      concat(
                        "Complete all missing details for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "factor"
                      ),
                      null
                    ),
                    /*Factor Date Validation*/
                    if(
                      or(
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) > todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationDueDate]
                          ),

                        ),
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) < todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          )
                        )
                      ),
                      concat(
                        "Enter a valid due date for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "factor"
                      ),
                      null
                    )
                  }
                ),
                null
              ),
              if(
                rule!AS_CO_UT_isNotBlank(
                  ri!evaluationSubfactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                ),
                #"SYSTEM_SYSRULES_forEach"(
                  items: rule!AS_CO_UT_filterRecordByFieldValues(
                    records: ri!evaluationSubfactors,
                    field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
                    values: true
                  ),
                  expression: {
                    /*Any Subfactor fields null*/
                    if(
                      fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorFieldsBlank],
                      concat(
                        "Complete all missing details for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "subfactor"
                      ),
                      null
                    ),
                    /*Subfactor Date Validation*/
                    if(
                      or(
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) < todate(
                            ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationStartDate]
                          ),

                        ),
                        and(
                          rule!AS_CO_UT_isNotBlank(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ),
                          rule!AS_CO_UT_isNotBlank(
                            todate(
                              index(
                                index(
                                  ri!evaluationFactors,
                                  wherecontains(
                                    fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId],
                                    ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                                  ),
                                  null
                                ),
                                recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate,
                                null
                              )
                            )
                          ),
                          todate(
                            fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate]
                          ) > todate(
                            index(
                              index(
                                ri!evaluationFactors,
                                wherecontains(
                                  fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId],
                                  ri!evaluationFactors[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                                ),
                                null
                              ),
                              recordType!AS_GSS_Criteria_SYNCEDRECORD.dueDate,
                              null
                            )
                          )
                        )
                      ),
                      concat(
                        "Enter a valid due date for the",
                        " ",
                        char(34),
                        index(
                          fv!item,
                          recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaName,
                          null
                        ),
                        char(34),
                        " ",
                        "subfactor"
                      ),
                      null
                    )
                  }
                ),
                null
              )
            }
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateFactors",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Weights*/
      a!map(
        sectionName: "Weights",
        message: a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            if(
              ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isWeightedFactorsRequired],
              rule!AS_GSS_VD_validateFactorWeightsWithRecord(
                evaluationFactors: ri!evaluationFactors,
                evaluationSubFactors: ri!evaluationSubfactors,
                i18nData: ri!i18nData,
                evaluation: ri!evaluation
              ),
              null
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateWeights",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Teams*/
      a!map(
        sectionName: "Teams",
        message: /*This can be handled by */
        /*1. adding custom field in teams record to hold the aggreagetd count of members in that team.*/
        a!defaultValue(
          rule!AS_CO_UT_rejectNullValuesFromArray(
            #"SYSTEM_SYSRULES_forEach"(
              items: rule!AS_CO_UT_filterRecordByFieldValues(
                records: ri!evaluatorTeams,
                field: recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.isActive,
                values: true
              ),
              expression: if(
                fv!item[recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.membersCount] = 0,
                concat(
                  "Add team members to the",
                  " ",
                  char(34),
                  index(
                    fv!item,
                    recordType!AS_GSS_EvaluatorTeam_SYNCEDRECORD.teamName,
                    null
                  ),
                  char(34),
                  " ",
                  "team"
                ),
                null
              )
            )
          ),
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateTeams",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      ),
      /*Assignment*/
      a!map(
        sectionName: "Assignment",
        message: if(
          or(
            #"SYSTEM_SYSRULES_forEach"(
              items: {
                ri!evaluationFactors,
                ri!evaluationSubfactors
              },
              expression: rule!AS_GSS_UT_checkIfNullOrZero(
                fv!item[recordType!AS_GSS_Criteria_SYNCEDRECORD.factorAssignmentCount]
              )
            )
          ),
          "Assign due dates and Evaluators to all Factors",
          null
        ),
        parentActionConfig: {
          a!map(
            actionKey: "updateAssignment",
            identifier: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
          )
        },
        childActionConfig: {}
      )
    },
    local!filteredMissingItems: rule!AS_CO_UT_rejectNullValuesFromArray(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!missingItems,
        expression: if(
          a!isNullOrEmpty(fv!item.message),
          null,
          fv!item
        )
      )
    ),
    local!filteredMissingItems
  ),
  {}
)

