    local!vendorName: if(
      rule!AS_CO_UT_isBlank(local!vendor),
      "",
      local!vendor.legalName
    ),
    local!oldVendorMappedFactors: rule!AS_CO_UT_filterCdtByField(
      cdt: ri!evaluationCriterias,
      field: "criteriaId",
      value: split(
        rule!AS_CO_UT_filterCdtByField(
          cdt: fv!item.simpleFieldChanges,
          field: "fieldName",
          value: "factorIds"
        ).oldValue,
        ","
      )
    ),
    local!newVendorMappedFactors: rule!AS_CO_UT_filterCdtByField(
      cdt: ri!evaluationCriterias,
      field: "criteriaId",
      value: split(
        rule!AS_CO_UT_filterCdtByField(
          cdt: fv!item.simpleFieldChanges,
          field: "fieldName",
          value: "factorIds"
        ).newValue,
        ","
      )
    ),
    local!factor: rule!AS_CO_UT_filterCdtByField(
