        /*Audit comment for Update doc visibility RA*/
        {
          rule!AS_GSS_DSP_displayAuditActionCodeText(
            actionCode: fv!item.auditActionCode,
            i18nData: ri!i18nData
          ),
          " " & "association of" & " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_filterCdtByField(
              cdt: fv!item.simpleFieldChanges,
              field: "fieldName",
              value: "docType"
            ).newValue,
            style: "STRONG",
            color: "SECONDARY",
            showWhen: rule!AS_CO_UT_isNotBlank(
              rule!AS_CO_UT_filterCdtByField(
                cdt: fv!item.simpleFieldChanges,
                field: "fieldName",
                value: "docType"
              )
            )
          ),
          " ",
          "document",
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!docName, style: "STRONG"),
          " ",
          "for",
          " ",
          "Vendor",
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!vendorName, style: "STRONG"),
          " ",
