            text: {
              "from" & " ",
              if(
                rule!AS_CO_UT_isBlank(local!oldVendorMappedFactors),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "blank",
                  color: "SECONDARY"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"SYSTEM_SYSRULES_forEach"(
                    items: local!oldVendorMappedFactors,
                    expression: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: concat(
                        if(
                          rule!AS_CO_UT_isBlank(fv!item.parentCriteriaId),
                          "Factor" & " ",
                          "Subfactor" & " "
                        ),
                        index(fv!item, "criteriaName", null),
                        if(
                          fv!isLast,
                          "",
                          if(
                            fv!index = count(local!oldVendorMappedFactors) - 1,
                            " " & "and" & " ",
                            ", "
                          )
                        )
                      )
                    )
                  )
                )
              ),
              " " & "to" & " ",
              if(
                rule!AS_CO_UT_isBlank(local!newVendorMappedFactors),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "blank",
                  color: "SECONDARY"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"SYSTEM_SYSRULES_forEach"(
                    items: local!newVendorMappedFactors,
                    expression: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: concat(
                        if(
                          rule!AS_CO_UT_isBlank(fv!item.parentCriteriaId),
                          "Factor" & " ",
                          "Subfactor" & " "
                        ),
                        index(fv!item, "criteriaName", null),
                        if(
                          fv!isLast,
                          ".",
                          if(
                            fv!index = count(local!newVendorMappedFactors) - 1,
                            " " & "and" & " ",
                            ", "
                          )
                        )
                      )
                    )
                  )
                )
              )
            }
