            text: " " & "document(s)",
            style: "",
            color: ""
          ),
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(text: local!docName, style: "STRONG"),
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: " " & "for" & " "
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: if(
                  rule!AS_CO_UT_isBlank(local!vendor),
                  if(
                    rule!AS_CO_UT_isBlank(local!factor.parentCriteriaId),
                    "Factor" & " ",
                    "Subfactor" & " "
                  ),
