   'type!{http://www.appian.com/ae/types/2009}Dictionary?list',
  if(
    rule!AS_CO_UT_isBlank(ri!objects),
    {},

    a!localVariables(
      local!dateTimes: if(
        rule!AS_CO_UT_isBlank(ri!dateTimeField),
        ri!objects,
        rule!AS_CO_UT_indexPath(
          cdt: ri!objects,
          field: ri!dateTimeField
        )
      ),

      local!dates: todate(local!dateTimes),

      local!uniqueDatesSorted: rule!AS_CO_UT_sortArray(
        array: rule!AS_CO_UT_distinct(value: local!dates),
        ascending: ri!ascending
      ),

      #"SYSTEM_SYSRULES_forEach"(
        items: local!uniqueDatesSorted,
        expression: {
          date: fv!item,
          objects: ri!objects[wherecontains(fv!item, local!dates)]
        }
      )
    )
  )
)

