        if(
          /*Ref Rating Label*/
          contains(
            { "refRatingType.refRatingId" },
            ri!fieldName
          ),
          5,
          if(
            /*State*/
            contains({ "vendorState.stateId" }, ri!fieldName),
            6,
            if(
              /*Country*/
              contains(
