data.sail_code:
/*to calculate due date*/
if(
  rule!AS_CO_UT_isBlank(ri!task.dueDate),
  if(
    /*Business days*/
    cons!AS_GSS_TMG_BOL_BUSINESS_DAYS_ONLY,
    if(
      rule!AS_CO_UT_isBlank(ri!task.noOfDaysFromStart),
      null,
      if(
        ri!task.noOfDaysFromStart <= 261,
        todate(caladddays(today(), ri!task.noOfDaysFromStart)),
        /* to calculate due date when no of days greater than 261 ( more than an year of working days)*/
        todate(
          reduce(
            fn!caladddays,
            merge({ today() }, { 0 }),
            {
              repeat(
                quotient(ri!task.noOfDaysFromStart, 261),
                261
              ),
              mod(ri!task.noOfDaysFromStart, 261)
            }
          )
        )
      )
    ),
    /*Non Business days*/
    todate(now() + ri!task.noOfDaysFromStart)
  ),
  ri!task.dueDate
)

