a!localVariables(
  /*The maximum number of users one loop can process*/
  /*-- For instance, if we were using a batch size of 25 with a max of 1,000 processes per loop*/
  /*this would return 25,000*/
  local!maxNumOfUsersOneLoopCanProcess: cons!AS_GAM_INT_USER_SYNC_USERNAME_BATCH_SIZE * cons!AS_GAM_INT_USER_SYNC_MNI_BATCH_SIZE,
  /*Calculates the start index to use based on the loopIncrement and the instanceIndex*/
  /*For instance, if we did a batch of 25 users per sub process and 1,000 per loop*/
  /*-- If this was loop 1, instanceIndex 1: Return 1*/
  /*-- If this was loop 1, instanceIndex 2: Return 26*/
  /*-- If this was loop 2, instanceIndex 1: Return 25,001*/
  /*-- If this was loop 2, instanceIndex 2: Return 25,026*/
  local!startIndexToUse: (ri!loopIncrement - 1) * local!maxNumOfUsersOneLoopCanProcess + (ri!instanceIndex - 1) * cons!AS_GAM_INT_USER_SYNC_USERNAME_BATCH_SIZE + 1,
  /*Return the username list to process based on the startIndex*/
  if(
    ri!returnDeactivatedUsers,
    rule!AS_GAM_UT_getAllDeactivatedAppianUsers(
      startIndex: local!startIndexToUse,
      batchSize: cons!AS_GAM_INT_USER_SYNC_USERNAME_BATCH_SIZE,
      entityMap: ri!entityMap
    ),
    a!groupMembers(
      group: ri!group,
      memberType: "USER",
