  typeof(ri!cdt),
  index(
    a!flatten(ri!cdt),
    rule!AS_CO_UT_returnCdtIndicesByField(
      cdt: ri!cdt,
      field: ri!field,
      value: ri!value
    ),
    {}
  )
)

