data.sail_code:
a!localVariables(
  local!requestBody: if(
    rule!AS_GSS_UT_isNotBlank(value: ri!request.body),
    #"SYSTEM_SYSRULES_fromJson_v1"(ri!request.body),
    {}
  ),
  a!match(
    value: a!map(
      vendorId: index(local!requestBody, "vendorId", null),
      legalName: index(local!requestBody, "legalName", null),
      noticeId: index(local!requestBody, "noticeId", null),
      proposalId: index(local!requestBody, "proposalId", null),
      proposalVersion: index(
        local!requestBody,
        "proposalVersion",
        null
      ),
      proposalAction: index(
        local!requestBody,
        "proposalAction",
        null
      ),
      proposalActionTimestamp: index(
        local!requestBody,
        "proposalActionTimestamp",
        null
      ),

    ),
    whenTrue: or(
      rule!AS_GSS_UT_isBlank(value: index(fv!value, "vendorId", null)),
      rule!AS_GSS_UT_isBlank(value: index(fv!value, "legalName", null)),
      rule!AS_GSS_UT_isBlank(value: index(fv!value, "noticeId", null)),
      rule!AS_GSS_UT_isBlank(
        value: index(fv!value, "proposalAction", null)
      ),
      rule!AS_GSS_UT_isBlank(
        value: index(
          fv!value,
          "proposalActionTimestamp",
          null
        )
      ),
      not(
        contains(
          {
            cons!AS_GSS_ENUM_PROPOSAL_ACTION_SUBMIT,
            cons!AS_GSS_ENUM_PROPOSAL_ACTION_RESUBMIT,
            cons!AS_GSS_ENUM_PROPOSAL_ACTION_WITHDRAW
          },
          index(fv!value, "proposalAction", null)
        )
      )
    ),
    then: #"SYSTEM_SYSRULES_httpResponse_v1"(
      statusCode: cons!AS_GSS_HTTP_STATUS_CODE_BAD_REQUEST,
      headers: {
        rule!AS_GSS_UT_getHttpHeaderForJsonContent()
      },
      body: #"SYSTEM_SYSRULES_toJson_v1"(
        {
          error: "INVALID_REQUEST",
          message: "The request cannot be processed as some of the mandatory inputs are incorrect or missing"
        }
      )
    ),
    default: #"SYSTEM_SYSRULES_startProcess3"(
      processModel: cons!AS_GSS_PM_RECEIVE_VENDOR_PROPOSAL_ACTION,
      /* A dictionary or map of parameters passed to the process model */
      processParameters: { requestBody: local!requestBody },
      isSynchronous: true(),
      /* This expression will run when the process is started (when run asynchronously) or completed (when run synchronously) */
      onSuccess: #"SYSTEM_SYSRULES_httpResponse_v1"(
        statusCode: cons!AS_GSS_HTTP_STATUS_CODE_SUCCESS,
        headers: {
          rule!AS_GSS_UT_getHttpHeaderForJsonContent()
        },
        body: if(
          a!isNotNullOrEmpty(fv!processInfo),
          #"SYSTEM_SYSRULES_toJson_v1"(
            /*Need to check if we return primary key after saving the request received in DB*/
            a!map(
              initiatedProcessInstanceId: fv!processInfo.pp.id,
              message: "Request processed successfully"
            )
          ),
          null()
        )
      ),
      /* This expression will run if the process fails to start */
      onError: #"SYSTEM_SYSRULES_httpResponse_v1"(
        statusCode: cons!AS_GSS_HTTP_STATUS_CODE_INTERNAL_SERVER_ERROR,
        headers: {
          rule!AS_GSS_UT_getHttpHeaderForJsonContent()
        },
        body: #"SYSTEM_SYSRULES_toJson_v1"(
          {
            error: "INTERNAL_SERVER_ERROR",
            message: "There was an error starting the process"
          }
        )
      ),
      /* If the process is started synchronously, this expression will run if the process does not complete in 30 seconds */
      onIncomplete: #"SYSTEM_SYSRULES_httpResponse_v1"(
        statusCode: cons!AS_GSS_HTTP_STATUS_CODE_TIMEOUT,
        headers: {
          rule!AS_GSS_UT_getHttpHeaderForJsonContent()
        },
        body: {
          error: "REQUEST_TIMED_OUT",
          message: "There was an error completing the process"
        }
      )
    )
  )
)

