sail_code:
if(
  rule!AS_CO_UT_isBlank(ri!tasks),
  {},
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!tasks,
    expression: rule!AS_CO_UT_updateCdtFields(
      cdt: fv!item,
      fields: {
        "taskStatus.taskStatusId",
        "modifiedBy",
        "modifiedDatetime",
        "availableDatetime",
        "dueDate"
      },
      newValues: {
        cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
        cons!AS_CO_TXT_GENERIC_SYSTEM_NAME,
        now(),
        now(),
        rule!AS_GSS_TMG_UT_calculateDuedateForTasksWithPrecedents(task: fv!item)
      }
    )
  )
)
