sail_code:
{
  rule!AS_CO_CPS_cardsAsSelection(
    choiceIcons: ri!refData.refIcon,
    choiceLabels: ri!refData.refLabel,
    choiceValues: ri!refData.refDataId,
    value: ri!task.review.decision.refDataId,
    saveInto: ri!task.review.decision.refDataId
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(),
      #"SYSTEM_SYSRULES_columnLayout"(
        width: "MEDIUM_PLUS",
        contents: {
          rule!AS_CO_INP_paragraphField(
            label: "Comment",
            instructions: rule!AS_CO_UT_instructionForCharactersEntered(
              value: ri!task.review.comment,
              maxCharacters: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
            ),
            required: if(
              contains(
                {
                  cons!AS_GSS_REF_ID_REVIEW_TYPE_REJECT,
                  cons!AS_GSS_REF_ID_REVIEW_TYPE_REQUEST_CHANGES
                },
                ri!task.review.decision.refDataId
              ),
              true,
              false
            ),
            value: ri!task.review.comment,
            validations: rule!AS_CO_VD_textValidation(
              value: ri!task.review.comment,
              maxLength: cons!AS_CO_ENUM_TEXT_LENGTH_LONG,
              maxLengthMessage: rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundle: ri!i18nData,
                bundleKey: "vld_Comments",
                arguments: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
              )
            ),
            saveInto: ri!task.review.comment,
            height: "SHORT"
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnLayout"()
    }
  )
}
