sail_code:
a!localVariables(
  local!cardLayouts: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_forEach"(
      items: ri!choiceValues,
      expression: a!localVariables(
        local!isSelected: rule!AS_CO_UT_compare(ri!value, ri!choiceValues[fv!index]),
        local!hasIcon: rule!AS_CO_UT_isNotBlank(index(ri!choiceIcons, fv!index, null)),
        local!hasLabel: rule!AS_CO_UT_isNotBlank(index(ri!choiceLabels, fv!index, null)),
        local!hasInstructions: rule!AS_CO_UT_isNotBlank(
          index(ri!choiceInstructions, fv!index, null)
        ),
        #"SYSTEM_SYSRULES_cardLayout"(
          style: if(
            local!isSelected,
            "ACCENT",
            if(ri!disabled, "STANDARD", "NONE")
          ),
          accessibilityText: rule!AS_CO_UT_displayCommonObjectBundleLabel(
            bundleKey: if(
              local!isSelected,
              "AS.CO.CommonObjects.acs_CardAsSelectionSelected",
              "AS.CO.CommonObjects.acs_CardAsSelectionUnselected"
            )
          ),
          marginBelow: "STANDARD",
          showborder: if(local!isSelected, false, true),
          height: "AUTO",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: if(
              local!isSelected,
              null,
              ri!choiceValues[fv!index]
            ),
            saveInto: ri!saveInto,
            showWhen: rule!AS_CO_UT_not(ri!disabled)
          ),
          contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            align: "CENTER",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: cons!AS_CO_TXT_CARRIAGE_RETURN,
                size: "SMALL"
              ),
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: if(
                  local!isSelected,
                  rule!AS_CO_UT_replaceNull(
                    nullableValue: ri!iconWhenSelected,
                    replacementValue: ri!choiceIcons[fv!index]
                  ),
                  ri!choiceIcons[fv!index]
                ),
                showWhen: local!hasIcon,
                size: "LARGE",
                color: "ACCENT"
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: {
                  cons!AS_CO_TXT_CARRIAGE_RETURN,
                  cons!AS_CO_TXT_CARRIAGE_RETURN
                },
                showWhen: and(local!hasIcon, local!hasLabel)
              ),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: ri!choiceLabels[fv!index],
                    showWhen: local!hasLabel,
                    size: "MEDIUM"
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: {
                      cons!AS_CO_TXT_CARRIAGE_RETURN,
                      ri!choiceInstructions[fv!index]
                    },
                    showWhen: local!hasInstructions,
                    size: "SMALL"
                  ),
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: {
                      cons!AS_CO_TXT_CARRIAGE_RETURN,
                      cons!AS_CO_TXT_CARRIAGE_RETURN
                    },
                    size: "SMALL"
                  ),

                },
                color: "STANDARD"
              )
            }
          )
        )
      )
    ),
    refreshAlways: true
  ),
  rule!AS_CO_UT_zINTERNAL_cardsToGridStyleLayout_handler(
    cardLayouts: local!cardLayouts,
    maxPerRow: ri!maxPerRow,
    align: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!align,
      replacementValue: "CENTER"
    ),
    columnWidth: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!columnWidth,
      replacementValue: "NARROW"
    )
  )
)
