sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Text',
  if(
    rule!AS_CO_UT_isBlank(ri!text),
    "",
    concat(
      rule!AS_CO_UT_replaceNull(nullableValue: ri!quote, replacementValue: """"),
      ri!text,
      rule!AS_CO_UT_replaceNull(nullableValue: ri!quote, replacementValue: """")
    )
  )
)
