sail_code:
#"SYSTEM_SYSRULES_gridField_v1"(
  labelPosition: "COLLAPSED",
  data: rule!AS_GSS_TMG_QE_getTaskCategories(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    pagingInfo: fv!pagingInfo,
    /*fetchTotalCount: true,*/
    triggerRefresh: ri!triggerRefresh
  ),
  columns: {
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Phase Name",
      sortField: "categoryName",
      value: rule!AS_CO_DSP_selectionLink(
        displayText: fv!row.categoryName,
        saveInto: a!save(ri!selectedTaskCategory, fv!row)
      ),
      helpTooltip: "Click a phase name to edit"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Last Modified",
      sortField: "modifiedDatetime",
      value: rule!AS_CO_DSP_displayTimeByUser(
        user: fv!row.modifiedBy,
        dateTime: fv!row.modifiedDatetime
      )
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      value: rule!AS_CO_DSP_removeIcon(
        caption: "Remove Phase",
        size: "MEDIUM",
        saveInto: {
          if(
            rule!AS_GSS_TMG_UT_checkIfCategoryCanBeDeactivated(category: fv!row),
            rule!AS_ADT_UT_startAuditProcess(
              newObjects: rule!AS_CO_UT_updateCdtFields(
                cdt: fv!row,
                fields: "isActive",
                newValues: false
              ),
              objectType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskCategory',
              objectDse: cons!AS_GSS_TMG_ENT_R_TASK_CATEGORY,
              auditDse: cons!AS_GSS_TMG_ENT_A_R_TASK_CATEGORY,
              auditConfig: rule!AS_GSS_TMG_ADT_BL_auditConfig_R_TaskCategory(),
              onSuccess: {
                rule!AS_CO_UT_triggerRefresh(triggerRefresh: ri!triggerRefresh),
                rule!AS_CO_UT_saveNull(
                  valueToNull: ri!deletionFailedCategoryName
                )
              },
              onError: {}
            ),
            a!save(
              ri!deletionFailedCategoryName,
              fv!row.categoryName
            )
          )
        },
        removalType: cons!AS_CO_ENUM_REMOVAL_ICON_TYPE_DELETE
      ),
      width: "ICON"
    )
  },
  initialSorts: #"SYSTEM_SYSRULES_sortInfo"(field: "categoryName", ascending: true),
  secondarySorts: #"SYSTEM_SYSRULES_sortInfo"(field: "categoryName", ascending: true),
  pageSize: cons!AS_CO_ENUM_PAGE_SIZE_LARGE
)
