sail_code:
a!localVariables(
  local!i18nData: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!subActionOptions: #"SYSTEM_SYSRULES_forEach"(
    items: {
      /*Order here MUST align with the order of interfaces in the choose function below*/
      "btn_AddTask",
      "btn_ImportTasks"
    },
    expression: rule!AS_GAM_CO_I18N_UT_displayLabel(
      bundle: local!i18nData,
      bundleKey: fv!item
    )
  ),
  local!selectedSubAction: 1,
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshOnVarChange: local!selectedSubAction
  ),
  local!selectedTaskRef,
  local!selectedTemplate,
  {
    if(
      rule!AS_CO_UT_isBlank(
        local!selectedTemplate
      ),
      choose(
        local!selectedSubAction,
        /*Order here MUST align with the order of options in local!subActionOptions above*/
        rule!AS_GSS_TMG_CPS_viewReferenceTasks(
          i18nData: local!i18nData,
          triggerRefresh: local!triggerRefresh,
          selectedTaskRef: local!selectedTaskRef,
          allowManageTaskRefs: true,
          selectedSubAction: local!selectedSubAction,
          subActionOptions: local!subActionOptions
        ),
        rule!AS_GSS_TMG_CP_manageReferenceTask(
          i18nData: local!i18nData,
          selectedTaskRef: local!selectedTaskRef,
          triggerRefresh: local!triggerRefresh,
          selectedSubAction: local!selectedSubAction
        ),
        rule!AS_GSS_TMG_FM_importReferenceTasks(
          i18nData: local!i18nData,
          selectedSubAction: local!selectedSubAction,
          selectedTemplate: local!selectedTemplate
        )
      ),
      rule!AS_GSS_TMG_CPS_manageReferenceTemplate(
        triggerRefresh: local!triggerRefresh,
        i18nData: local!i18nData,
        selectedTemplate: local!selectedTemplate
      )
    )
  }
)
