sail_code:
{
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          rule!AS_CO_DSP_displayHeader(
            headerText: "Approaches"
          )
        }
      )
    }
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          #"SYSTEM_SYSRULES_ButtonArrayLayout"(
            buttons: {
              rule!AS_CO_BTN_toolbarButton(
                icon: "PLUS",
                label: "Add Approach",

                saveInto: {
                  a!save(
                    target: ri!selectedTemplate,
                    value: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_Template'()
                  )
                },
                accessibilityText:"Click here to add process"
              )
            },
            align: "START"
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          rule!AS_CO_CP_searchField(
            value: ri!searchText,
            saveInto: ri!searchText,
            placeholder: rule!AS_GSS_TMG_pickerPlaceholder(
              i18nData: ri!i18nData,
              bundleKey: "lbl_Templates"
            )
          )
        },
        width: "MEDIUM"
      )
    },
    marginBelow: "NONE"
  ),
  rule!AS_GSS_TMG_GRD_templatesGrid(
    triggerRefresh: ri!triggerRefresh,
    selectedTemplate: ri!selectedTemplate,
    i18nData: ri!i18nData,
    searchText: ri!searchText,
    showHistory: ri!showHistory
  )
}
