sail_code:
a!localVariables(
  local!deletionFailedCategoryName: rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{http://www.appian.com/ae/types/2009}Text'
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_CO_DSP_displayHeader(
              headerText: "Phases"
            )
          }
        )
      }
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                rule!AS_CO_BTN_toolbarButton(
                  label: "Add Phase",
                  saveInto: {
                    a!save(
                      target: ri!selectedTaskCategory,
                      value: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskCategory'()
                    ),
                    rule!AS_CO_UT_saveNull(local!deletionFailedCategoryName)
                  },
                  icon: "PLUS"
                )
              },
              align: "START"
            )
          }
        ),

      },
      marginBelow: "NONE"
    ),
    rule!AS_CO_DSP_errorBanner(
      showWhen: rule!AS_CO_UT_isNotBlank(local!deletionFailedCategoryName),
      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: "ins_UnableToRemoveCategory",
        arguments: local!deletionFailedCategoryName
      ),
      secondaryLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: ri!i18nData,
        bundleKey: "lbl_AllTasksMustBeRemoved",
        arguments: local!deletionFailedCategoryName
      ),
      acknowledgementSaveInto: rule!AS_CO_UT_saveNull(
        valueToNull: local!deletionFailedCategoryName
      )
    ),
    rule!AS_GSS_TMG_GRD_taskCategoriesGrid(
      i18nData: ri!i18nData,
      selectedTaskCategory: ri!selectedTaskCategory,
      triggerRefresh: ri!triggerRefresh,
      deletionFailedCategoryName: local!deletionFailedCategoryName
    )
  }
)
